INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (442, 'Direttiva 1999/31/CE del Consiglio, del 26 aprile 1999, relativa alle discariche di rifiuti', 'Direttiva (UE)', '1999/31', 'Questa Direttiva ha l''obiettivo di prevenire o ridurre il più possibile le ripercussioni negative delle discariche sulle acque superficiali, le acque freatiche, il suolo, l’atmosfera o la salute umana. A tal fine introduce rigidi requisiti tecnici.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva relativa alle discariche di rifiuti', 'Direttiva (UE) - 1999/31', 'Direttiva (UE) 1999/31', NULL);

-- Record 27/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (442, 'Direttiva (UE) 2018/850 del Parlamento europeo e del Consiglio, del 30 maggio 2018, che modifica la direttiva 1999/31/CE relativa alle discariche di rifiuti', 'Direttiva (UE)', '2018/850', 'Le modifiche della Direttiva 2018/850 intendono migliorare la gestione dei rifiuti nell’Unione, così come tutelare e migliorare la qualità dell’ambiente, proteggere la salute umana, garantire un utilizzo accorto, efficiente e razionale delle risorse naturali, promuovere i principi dell’economia circolare, incrementare l’efficienza energetica e ridurre la dipendenza dell’Unione dalle risorse importate.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica la direttiva 1999/31/CE relativa alle discariche di rifiuti', 'Direttiva (UE) - 2018/850', 'Direttiva (UE) 2018/850', NULL);

-- Record 28/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (442, 'Direttiva (UE) 2018/851 del Parlamento europeo e del Consiglio, del 30 maggio 2018, che modifica la direttiva 2008/98/CE relativa ai rifiuti', 'Direttiva (UE)', '2018/851', 'Questa direttiva modifica la direttiva 2008/98/CE che stabilisce misure volte a proteggere l’ambiente e la salute umana evitando o riducendo la produzione di rifiuti, gli effetti negativi della produzione e della gestione dei rifiuti, riducendo gli effetti generali dell’uso delle risorse e migliorandone l’efficienza, che costituiscono elementi fondamentali per il passaggio a un’economia circolare e per assicurare la competitività a lungo termine dell’Unione.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica la direttiva 2008/98/CE relativa ai rifiuti', 'Direttiva (UE) - 2018/851', 'Direttiva (UE) 2018/851', NULL);

-- Record 29/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (442, 'Regolamento Delegato (UE) 2021/2178 della Commissione del 6 luglio 2021 che integra il regolamento (UE) 2020/852 del Parlamento europeo e del Consiglio precisando il contenuto e la presentazione delle informazioni che le imprese soggette all’articolo 19 bis o all’articolo 29 bis della direttiva 2013/34/UE devono comunicare in merito alle attività economiche ecosostenibili e specificando la metodologia per conformarsi a tale obbligo di informativa', 'Regolamento (UE)', '2021/2178', 'Questo regolamento 2021/2178 integra il regolamento (UE) 2020/852 precisando il contenuto e la presentazione delle informazioni che le imprese soggette all’articolo 19 bis o all’articolo 29 bis della direttiva 2013/34/UE devono comunicare in merito alle attività economiche ecosostenibili e specificando la metodologia per conformarsi a tale obbligo di informativa', 'I regolamenti sono atti giuridici definiti nell’articolo 288 del trattato sul funzionamento dell’Unione europea (TFUE). Hanno portata generale, sono vincolanti in tutti i loro elementi e direttamente applicabili negli Stati membri dell’Unione. I regolamenti rientrano nel diritto derivato dell’Unione. Sono adottati dalle istituzioni dell’Unione europea sulla base dei trattati. Un regolamento ha carattere vincolante in tutti i suoi elementi, ha portata generale ed è rivolto a categorie astratte di persone, non a specifici individui. 
 https://eur-lex.europa.eu/IT/legal-content/summary/european-union-regulations.html', 'Regolamento che integra il regolamento (UE) 2020/852', 'Regolamento (UE) - 2021/2178', 'Regolamento (UE) 2021/2178', NULL);

-- Record 30/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (442, 'Green Deal Europeo (EGD)', 'Strategia (UE)', 'Green Deal Europeo', 'Il Green Deal europeo è un pacchetto di iniziative strategiche che mira ad avviare l''UE sulla strada di una transizione verde, con l''obiettivo ultimo di raggiungere la neutralità climatica entro il 2050.', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Strategia dell''UE per conseguire l''obiettivo della neutralità climatica entro il 2050', 'Strategia (UE) - Green Deal Europeo', 'Green Deal Europeo', 'Green Deal Europeo: Strategia dell''UE per conseguire l''obiettivo della neutralità climatica entro il 2050');

