INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (444, 'ISO 45003: Gestione della salute e sicurezza sul lavoro — Salute psicologica e sicurezza sul lavoro', 'ISO', '45003', 'L''ISO 45003:2021 è uno standard internazionale che fornisce linee guida per la gestione dei rischi psicosociali sul posto di lavoro. Si tratta di una specificazione complementare alla norma ISO 45001, che è lo standard per i sistemi di gestione della salute e sicurezza sul lavoro (OH&S). L''ISO 45003:2021 si concentra sui fattori che influenzano la salute psicologica e il benessere dei lavoratori, affrontando in particolare i rischi psicosociali.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Gestione della salute e sicurezza sul lavoro — Salute psicologica e sicurezza sul lavoro', 'ISO - 45003', 'ISO 45003', 'Gestione della salute e sicurezza sul lavoro — Salute psicologica e sicurezza sul lavoro');

