INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (446, 'SDGs: Goal 3 Salute e benessere', 'SDGs', 'Goal 3', 'Assicurare la salute e il benessere per tutti e per tutte le età', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Salute e benessere', 'SDGs - Goal 3', 'SDG 3.9', 'Entro il 2030, ridurre sostanzialmente il numero di decessi e malattie da sostanze chimiche pericolose e da contaminazione e inquinamento dell’aria, delle acque e del suolo');

-- Record 77/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (446, 'Regolamento (UE) 2020/852 del Parlamento Europeo e del 18 giugno 2020 relativo all’istituzione di un quadro che favorisce gli investimenti sostenibili e recante modifica del regolamento (UE) 2019/2088', 'Regolamento (UE)', '2020/852', 'Il regolamento stabilisce i criteri per determinare se un’attività economica possa considerarsi ecosostenibile, al fine di individuare il grado di ecosostenibilità di un investimento', 'I regolamenti sono atti giuridici definiti nell’articolo 288 del trattato sul funzionamento dell’Unione europea (TFUE). Hanno portata generale, sono vincolanti in tutti i loro elementi e direttamente applicabili negli Stati membri dell’Unione. I regolamenti rientrano nel diritto derivato dell’Unione. Sono adottati dalle istituzioni dell’Unione europea sulla base dei trattati. Un regolamento ha carattere vincolante in tutti i suoi elementi, ha portata generale ed è rivolto a categorie astratte di persone, non a specifici individui. 
 https://eur-lex.europa.eu/IT/legal-content/summary/european-union-regulations.html', 'Tassonomia UE: Regolamento relativo all’istituzione di un quadro che favorisce gli investimenti sostenibili e recante modifica del regolamento (UE) 2019/2088', 'Tassonomia UE - (2020/852)', 'Tassonomia UE (2020/852)', 'Tassonomia UE');

