INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (446, 'SDGs: Goal 11 Città e comunità sostenibili', 'SDGs', 'Goal 11', 'Rendere le città e gli insediamenti umani inclusivi, sicuri, duraturi e sostenibili', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Città e comunità sostenibili', 'SDGs - Goal 11', 'SDG 11.6', 'Entro il 2030, ridurre l’impatto ambientale negativo pro-capite delle città, prestando particolare attenzione alla qualità dell’aria e alla gestione dei rifiuti urbani e di altri rifiuti');

