INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (446, 'Piano d''azione dell''UE: "Verso l''inquinamento zero per l''aria, l''acqua e il suolo"', 'Piano Azione (UE)', 'Inquinamento Zero', 'Il piano d''azione per l''inquinamento zero dell''UE, adottato nel 2021, mira a ridurre l''inquinamento atmosferico e acustico a livelli non più considerati nocivi per la salute e per gli ecosistemi naturali.', 'Un piano di azione a livello dell''Unione Europea (UE) è un documento strategico che definisce obiettivi specifici e misure concrete per affrontare sfide o perseguire opportunità a livello comunitario. Questi piani sono sviluppati e implementati dall''UE per guidare l''azione politica e normativa su questioni di interesse comune tra gli Stati membri.', 'Piano Verso l''inquinamento zero per l''aria, l''acqua e il suolo"', 'Piano Azione (UE) - Inquinamento Zero', 'Piano Azione (UE) Inquinamento Zero', 'COM(2021) 400 - Piano d''azione dell''UE: "Verso l''inquinamento zero per l''aria, l''acqua e il suolo"');

-- Record 85/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (446, 'Linee guida globali OMS sulla qualità dell''aria', 'Linee Guida (Int.)', 'OMS', 'Le Linee guida globali OMS sulla qualità dell''aria sono strumenti fondamentali per proteggere la salute pubblica, guidare i decisori politici e aumentare la consapevolezza pubblica sui rischi dell''inquinamento atmosferico. Forniscono valori soglia scientificamente supportati per diversi inquinanti chiave, che se rispettati, possono ridurre significativamente l''incidenza di malattie legate all''inquinamento dell''aria.', 'Le linee guida sono documenti che forniscono indicazioni e raccomandazioni su come applicare le leggi esistenti o sviluppare politiche in un determinato settore.', 'Linee guida globali sulla qualità dell''aria', 'Linee Guida - OMS', 'LGG OMS qualità dell''aria', 'Linee guida globali OMS sulla qualità dell''aria');

-- Record 86/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (446, 'Legge 447/1995 - Legge quadro sull''inquinamento acustico', 'Legge (IT)', '447/1995', 'La legge 447/1995 stabilisce i princìpi fondamentali in materia di tutela dell''ambiente esterno e dell''ambiente abitativo dall''inquinamento acustico, ai sensi e per gli effetti dell''articolo 117 della Costituzione (italiana).', 'Il termine legge viene usato nel diritto con una molteplicità di significati, si distingue in:
la norma giuridica; l’atto normativo (legge in senso materiale); l’atto giuridico attraverso il quale il potere legislativo esercita la sua funzione (legge in senso formale); l’insieme delle norme giuridiche che costituiscono l’ordinamento giuridico, ossia il diritto oggettivo. Per maggiori informazioni accedere al link:
https://www.governo.it/it/costituzione-italiana/principi-fondamentali/2839', 'Inquinamento acustico', 'Legge (IT) - 447/1995', 'Legge 447/1995', 'Legge quadro sull''inquinamento acustico');

-- Record 87/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (446, 'ISO 9001: Certificazione del Sistema di Gestione per la Qualità', 'ISO', '9001', 'La norma ISO 9001 specifica i requisiti per l''istituzione, il mantenimento e il miglioramento continuo di un sistema di gestione della qualità, coprendo una vasta gamma di argomenti, tra cui:
 - Contesto dell''organizzazione, - Leadership, - Pianificazione, - Supporto, - Valutazione delle prestazioni, - Miglioramento', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Certificazione del Sistema di Gestione per la Qualità', 'ISO - 9001', 'ISO 9001', 'Certificazione del Sistema di Gestione per la Qualità');

