INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (446, 'GRI 403- Salute e sicurezza sul lavoro (2018)', 'GRI', '403', 'Questo Standard tratta il tema della salute e sicurezza sul lavoro. Contiene informative per le organizzazioni che devono rendicontare informazioni sui loro impatti correlati alla salute e sicurezza sul lavoro e sul modo in cui li gestiscono.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Salute e sicurezza sul lavoro (2018)', 'GRI - 403 (2018)', 'GRI 403-10 (c) (2018)', '(Malattia professionale) L’organizzazione deve rendicontare le seguenti informazioni: I pericoli sul lavoro che, se non controllati, presentano un rischio di malattia
professionale [...]');

