INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (448, 'ESRS E2- Inquinamento', 'ESRS', 'E2', 'Questo Standard stabilisce i requisiti di Informativa GRI  relativi ai seguenti aspetti della sostenibilità: inquinamento dell''aria, dell''acqua, del suolo, sostanze preoccupanti, comprese le sostanze estremamente preoccupanti.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Inquinamento', 'ESRS - E2', 'ESRS (E2) E2-4 - 28 a  AR 21-22', 'Inquinamento dell''aria, dell''acqua e del suolo [dimensioni multiple: a livello di sito o per tipo di fonte, per settore o per area geografica]');

