INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (452, 'Direttiva (UE) 2024/1500 del Parlamento europeo e del Consiglio, del 14 maggio 2024, sulle norme riguardanti gli organismi per la parità nel settore della parità di trattamento e delle pari opportunità tra donne e uomini in materia di occupazione e impiego, e che modifica le direttive 2006/54/CE e 2010/41/UE', 'Direttiva (UE)', '2024/1500', 'La Direttiva (UE) 2024/1500 stabilisce norme riguardanti gli organismi per la parità nel settore della parità di trattamento e delle pari opportunità tra donne e uomini in materia di occupazione e impiego. Questa direttiva mira a rafforzare l''efficacia e l''indipendenza degli organismi per la parità, con l''obiettivo di garantire un''applicazione più rigorosa del principio di parità di trattamento derivante dalle direttive precedenti, in particolare la 2006/54/CE e la 2010/41/UE.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Sulle norme riguardanti gli organismi per la parità nel settore della parità di trattamento e delle pari opportunità tra donne e uomini in materia di occupazione e impiego', 'Direttiva (UE) - 2024/1500', 'Direttiva (UE) 2024/1500', NULL);

