INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (454, 'Principi di Corporate Governance G20/OECD (2023)- Principio V', 'Principi G20/OECD', 'Principio V', 'Il principio V stabilische che il quadro di governance aziendale dovrebbe garantire l''orientamento strategico dell''azienda, un monitoraggio efficace della gestione da parte del consiglio di amministrazione e la responsabilità del consiglio nei confronti dell''azienda e degli azionisti.', 'I Principi di Corporate Governance G20/OCSE sono lo standard internazionale per il governo societario. I Principi aiutano i responsabili politici a valutare e migliorare il quadro legale, regolamentare e istituzionale per il governo societario, con l''obiettivo di supportare l''efficienza economica, la crescita sostenibile e la stabilità finanziaria. Per maggiori informazioni vedere: https://legalinstruments.oecd.org/public/doc/322/322.en.pdf', 'Le responsabilità del consiglio di amministrazione', 'Principi - Corporate Governance G20/OECD - V', 'Principi G20/OECD: V.D.6', 'Garantire un processo formale e trasparente per la nomina e l''elezione del consiglio di amministrazione.');

-- Record 217/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (454, 'SDGs: Goal 10 Ridurre le disuguaglianze', 'SDGs', 'Goal 10', 'Ridurre l’ineguaglianza all’interno di e fra le Nazioni', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Ridurre le disuguaglianze', 'SDGs - Goal 10', 'SDG 10.3', 'Assicurare pari opportunità e ridurre le disuguaglianze nei risultati, anche eliminando leggi, politiche e pratiche discriminatorie e promuovendo legislazioni, politiche e azioni appropriate a tale proposito');

-- Record 218/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (454, 'GRI 2- Informativa generale (2021)', 'GRI', '2', 'GRI che contiene le informative per le organizzazioni che devono fornire informazioni sulle loro prassi di rendicontazione, sulle attività e sui lavoratori, sulla governance, sulla strategia, sulle politiche e sulle  pratiche e sul coinvolgimento degli stakeholder', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Informativa generale (2021)', 'GRI - 2 (2021)', 'GRI 2-10 (2021)', 'Nomina e selezione del massimo organo di governo');

-- Record 219/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (454, 'Direttiva (UE) 2024/1500 del Parlamento europeo e del Consiglio, del 14 maggio 2024, sulle norme riguardanti gli organismi per la parità nel settore della parità di trattamento e delle pari opportunità tra donne e uomini in materia di occupazione e impiego, e che modifica le direttive 2006/54/CE e 2010/41/UE', 'Direttiva (UE)', '2024/1500', 'La Direttiva (UE) 2024/1500 stabilisce norme riguardanti gli organismi per la parità nel settore della parità di trattamento e delle pari opportunità tra donne e uomini in materia di occupazione e impiego. Questa direttiva mira a rafforzare l''efficacia e l''indipendenza degli organismi per la parità, con l''obiettivo di garantire un''applicazione più rigorosa del principio di parità di trattamento derivante dalle direttive precedenti, in particolare la 2006/54/CE e la 2010/41/UE.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Sulle norme riguardanti gli organismi per la parità nel settore della parità di trattamento e delle pari opportunità tra donne e uomini in materia di occupazione e impiego', 'Direttiva (UE) - 2024/1500', 'Direttiva (UE) 2024/1500', NULL);

-- Record 220/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (454, 'Legge 12 luglio 2011, n. 120 - Modifiche al testo unico delle disposizioni in materia di intermediazione finanziaria, di cui al Decreto Legislativo 24 febbraio 1998, n. 58, concernenti la parità di accesso agli organi di amministrazione e di controllo delle società quotate in mercati regolamentati.', 'Legge (IT)', '120/2011', 'La legge 120/2011 promuove l''equilibrio di genere nelle posizioni di leadership delle aziende quotate.', 'Il termine legge viene usato nel diritto con una molteplicità di significati, si distingue in:
la norma giuridica; l’atto normativo (legge in senso materiale); l’atto giuridico attraverso il quale il potere legislativo esercita la sua funzione (legge in senso formale); l’insieme delle norme giuridiche che costituiscono l’ordinamento giuridico, ossia il diritto oggettivo. Per maggiori informazioni accedere al link:
https://www.governo.it/it/costituzione-italiana/principi-fondamentali/2839', 'Modifiche al testo unico delle disposizioni in materia di intermediazione finanziaria', 'Legge (IT) - 120/2011', 'Legge 120/2011', NULL);

