INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (456, 'Piano di Gestione delle Acque (2021- 2027)', 'Piano Nazionale (IT)', 'Gestione delle Acque', 'Il Piano di Gestione delle Acque, previsto dalla Direttiva 2000/60/CE, è lo strumento principale dell''Autorità di bacino distrettuale per raggiungere il buono stato ambientale dei corpi idrici e garantire una gestione sostenibile delle risorse idriche, riducendo l''inquinamento, prevenendo il deterioramento e migliorando gli ecosistemi acquatici e terrestri.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Piano di gestione delle acque 2021-2027', 'Piano Nazionale (IT) - Gestione delle Acque', 'Piano (IT) Gestione Acque', 'Piano di Gestione delle Acque (2021- 2027');

