INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (456, 'WEF- Metriche del Capitalismo degli Stakeholder 2020 P2-3 (Disponibilità di acqua dolce)', 'WEF', '4P: Metrica P2-3', 'Questa metrica si concentra sull''acqua dolce, essenziale per la salute dell''ambiente, lo sviluppo economico e come diritto umano fondamentale. Tuttavia, in regioni con scarsità d''acqua e infrastrutture inadeguate, questo diritto spesso non è garantito.', 'Il World Economic Forum è l''Organizzazione Internazionale per la Cooperazione Pubblico-Privata. L''WEF insieme ad altre istituzioni ha sviluppato un insieme di metriche comuni per la rendicontazione della creazione di valore sostenibile (21 metriche principali e 34 metriche e divulgazioni ampliate) raggruprate in 4 pilastri principali (Principle of Governance, Planet, Persone and Prosperity). Per maggiore informazioni sulle 4 Pilastri (Metriche) di sostenibilità del WEF andare su: https://www.weforum.org/stakeholdercapitalism/our-metrics/', '3- Disponibilità di acqua dolce', 'WEF (MSC2024) 4P- P2-3', 'WEF (MSC2024) 4P- P2-3 CM 1', 'Consumo e prelievo di acqua in aree a stress idrico');

-- Record 260/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (456, 'Taskforce on Nature-related Financial Disclosure Recommendations - A3.0', 'TNFD', 'Racommandazioni - A3.0', 'A3.0: Le raccomandazioni del TNFD guidano imprese e istituzioni finanziarie nel valutare, comunicare e gestire i propri impatti e rischi legati alla natura, in linea con il Target 15 del Quadro Globale per la Biodiversità (GBF).', 'La TNFD (Taskforce on Nature-related Financial Disclosures) è un''iniziativa globale che mira a sviluppare un quadro di riferimento per la gestione e la divulgazione dei rischi legati alla natura per le imprese e le istituzioni finanziarie.

Le raccomandazioni del TNFD guidano imprese e istituzioni finanziarie nel valutare, comunicare e gestire i propri impatti e rischi legati alla natura, in linea con il Target 15 del Quadro Globale per la Biodiversità (GBF).', 'Consumo e prelievo totale di acqua', 'TNFD -  Racommandazioni - A3.0', 'TNFD - A3.0', 'Consumo e prelievo totale di acqua.');

