INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (457, 'ISO 14046:2014: Impronta Idrica', 'ISO', '14046:2014', 'La ISO 14046:2014 è uno standard internazionale che fornisce linee guida per la valutazione e la comunicazione dell''impronta idrica di un''organizzazione, di un prodotto o di un servizio. Questo standard è parte della serie ISO 14000, che si occupa della gestione ambientale.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Impronta Idrica', 'ISO - 14046:2014', 'ISO 14046:2014', 'Impronta Idrica');

-- Record 269/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (457, 'Piano di Gestione delle Acque (2021- 2027)', 'Piano Nazionale (IT)', 'Gestione delle Acque', 'Il Piano di Gestione delle Acque, previsto dalla Direttiva 2000/60/CE, è lo strumento principale dell''Autorità di bacino distrettuale per raggiungere il buono stato ambientale dei corpi idrici e garantire una gestione sostenibile delle risorse idriche, riducendo l''inquinamento, prevenendo il deterioramento e migliorando gli ecosistemi acquatici e terrestri.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Piano di gestione delle acque 2021-2027', 'Piano Nazionale (IT) - Gestione delle Acque', 'Piano (IT) Gestione Acque', 'Piano di Gestione delle Acque (2021- 2027');

-- Record 270/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (457, 'Strategia Nazionale per lo Sviluppo Sostenibile (SNSvS)', 'Strategia Nazionale (IT)', 'SNSvS', 'In Italia rappresenta il quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali, declinando per il contesto nazionale gli Obiettivi di Sviluppo Sostenibile (SDG) delineati dall''ONU', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia Nazionale italiana per lo Sviluppo Sostenibile', 'SNSvS (2022)', 'SNSvS22- Pianeta II.5', 'Massimizzare l’efficienza idrica e adeguare i prelievi alla scarsità d’acqua');

