INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (457, 'Taskforce on Nature-related Financial Disclosure Recommendations - C3.0', 'TNFD', 'Racommandazioni - C3.0', 'C3.0: Le raccomandazioni del TNFD guidano imprese e istituzioni finanziarie nel valutare, comunicare e gestire i propri impatti e rischi legati alla natura, in linea con il Target 15 del Quadro Globale per la Biodiversità (GBF).', 'La TNFD (Taskforce on Nature-related Financial Disclosures) è un''iniziativa globale che mira a sviluppare un quadro di riferimento per la gestione e la divulgazione dei rischi legati alla natura per le imprese e le istituzioni finanziarie.

Le raccomandazioni del TNFD guidano imprese e istituzioni finanziarie nel valutare, comunicare e gestire i propri impatti e rischi legati alla natura, in linea con il Target 15 del Quadro Globale per la Biodiversità (GBF).', 'Prelievo e consumo di acqua da aree soggette a scarsità idrica.', 'TNFD -  Racommandazioni - C3.0', 'TNFD - C3.0', 'Prelievo e consumo di acqua da aree soggette a scarsità idrica.');

