INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (459, 'GRI 303- Acqua ed effluenti (2018)', 'GRI', '303', 'Questo Standard tratta il tema dell''acqua e degli effluenti. Contiene informative per le organizzazioni che devono rendicontare informazioni sui loro impatti correlati all''acqua e la relativa gestione.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Acqua ed effluenti (2018)', 'GRI - 303 (2018)', 'GRI 303-1 (2018)', 'Interazioni con l''acqua come risorsa condivisa');

-- Record 289/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (459, 'ESRS E3- Risorse idriche e marine', 'ESRS', 'E3', 'Questo Standard stabilisce i requisiti di Informativa relativi alle risorse idriche e marine. Per quanto riguarda "l''acqua", questo standard copre le acque superficiali e sotterranee. Include i requisiti di informativa sul consumo idrico nelle attività, nei prodotti e nei servizi dell''impresa, nonché le informazioni relative ai prelievi e agli scarichi idrici.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Risorse idriche e marine', 'ESRS - E3', 'ESRS (E3) E3-5 33 a', 'Divulgazione di informazioni qualitative sugli effetti finanziari potenziali di rischi e opportunità significativi derivanti da impatti relativi alle risorse idriche e marine');

