INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (459, 'Strategia Nazionale per l''Economia Circolare', 'Strategia Nazionale (IT)', 'Economia Circolare', 'La Strategia Nazionale per l’Economia Circolare è un piano elaborato dall''Italia per promuovere un modello economico sostenibile, in cui le risorse vengono utilizzate in modo efficiente e i rifiuti vengono ridotti al minimo.', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia per l''Economia Circolare', 'Strategia (IT) Economia Circolare', 'Strategia (IT) Economia Circolare', 'Strategia Nazionale per l''Economia Circolare');

-- Record 301/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (459, 'Piano d''azione per l''economia circolare 2020 (CEAP 2020 - EU)', 'Piano Azione (UE)', 'Economia Circolare', 'Questo piano d''azione per l''economia circolare stabilisce un programma orientato al futuro per costruire un''Europa più pulita e competitiva in co-creazione con gli operatori economici, i consumatori, i cittadini e le organizzazioni della società civile. Mira ad accelerare il profondo cambiamento richiesto dal Green Deal europeo, sulla base delle azioni in materia di economia circolare attuate dal sin dal 2015.', 'Un piano di azione a livello dell''Unione Europea (UE) è un documento strategico che definisce obiettivi specifici e misure concrete per affrontare sfide o perseguire opportunità a livello comunitario. Questi piani sono sviluppati e implementati dall''UE per guidare l''azione politica e normativa su questioni di interesse comune tra gli Stati membri.', 'Piano per l''Economia circolare 2020', 'Piano Azione (UE) - Economia Circolare', 'Piano Azione (UE) Economia Circolare', 'Piano d''azione per l''economia circolare 2020');

-- Record 302/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (459, 'ISO 46001: Sistemi di gestione dell''efficienza idrica', 'ISO', '46001', 'L''ISO 46001:2019 stabilisce i requisiti per un sistema di gestione dell''efficienza idrica (Water Efficiency Management System - WEMS). Questa norma fornisce un quadro per le organizzazioni che desiderano migliorare la gestione delle risorse idriche, ridurre il consumo di acqua e aumentare l''efficienza nell''uso di questa risorsa.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Sistemi di gestione dell''efficienza idrica', 'ISO - 46001', 'ISO 46001', 'Sistemi di gestione dell''efficienza idrica');

