INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (461, 'Protocollo di Kyoto della Convenzione Quadro delle Nazioni Unite sui Cambiamenti Climatici', 'Protocollo (Int.)', 'Cambiamenti Climatici (Protocollo Kyoto)', 'Il protocollo di Kyoto, che fa seguito alla convenzione quadro delle Nazioni Unite sui cambiamenti climatici, è uno dei più importanti strumenti giuridici internazionali volti a combattere i cambiamenti climatici. Esso contiene gli impegni dei paesi industrializzati a ridurre le emissioni di alcuni gas ad effetto serra, responsabili del riscaldamento del pianeta.', 'Un protocollo è un tipo di accordo formale tra Stati o altre entità sovrane che integra, modifica o specifica ulteriormente un trattato esistente. I protocolli sono strumenti giuridici che possono avere diverse funzioni e scopi, tra cui l''espansione di disposizioni di un trattato, la creazione di nuovi obblighi o la regolamentazione di questioni specifiche non completamente coperte dal trattato originale.', 'Quadro delle Nazioni Unite sui Cambiamenti Climatici', 'Protocollo - Cambiamenti Climatici (Protocollo Kyoto)', 'Protocollo Kyoto', 'Convenzione Quadro delle Nazioni Unite sui Cambiamenti Climatici');

-- Record 315/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (461, 'Accordo di Parigi sui cambiamenti climatici', 'Trattato internazionale', 'Accordo di Parigi sui Cambiamenti climatici', 'L''accordo di Parigi è un trattato internazionale che presenta un piano d''azione per limitare il riscaldamento globale. L’accordo di Parigi, firmato da 194 paesi e dall’UE, mira a limitare il riscaldamento globale al di sotto di 2°C e a proseguire gli sforzi per circoscriverlo a 1,5°C al fine di evitare le conseguenze catastrofiche del cambiamento climatico.', 'L''accordo internazionale può essere considerato come un contratto stipulato tra due o più Stati, diretto a regolare una determinata sfera di rapporti tra i contraenti. La sua formazione è regolata da una serie di norme consuetudinarie e convenzionali che rientrano nel diritto pubblico internazionale.', 'Accordo sui cambiamenti climatici', 'Trattato internazionale - Accordo di Parigi sui Cambiamenti climatici', 'Accordo Parigi (2015)', 'Accordo che mira a limitare il riscaldamento globale al di sotto di 2°C');

-- Record 316/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (461, 'Strategia Nazionale Adattamento Cambiamenti Climatici (2015)', 'Strategia Nazionale (IT)', 'Adattamento Cambiamenti Climatici (2015)', 'La Strategia Nazionale di Adattamento ai Cambiamenti Climatici ha l''obiettivo di elaborare una visione nazionale sui percorsi comuni da intraprendere per far fronte ai cambiamenti climatici, contrastando e attenuando i loro impatti. La strategia mira a preparare e proteggere l''Italia dagli effetti dei cambiamenti climatici attraverso una serie di misure integrate e coordinate per aumentare la resilienza e la capacità di adattamento del paese.', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia per l''adattamento Cambiamenti Climatici', 'Strategia Nazionale - Adattamento Cambiamenti Climatici (2015)', 'SNAC (2015)', 'Strategia Nazionale Adattamento Cambiamenti Climatici');

-- Record 317/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (461, 'Regolamento Delegato (UE) 2021/2139 della Commissione del 4 giugno 2021 che integra il regolamento (UE) 2020/852 del Parlamento europeo e del Consiglio fissando i criteri di vaglio tecnico che consentono di determinare a quali condizioni si possa considerare che un''attività economica contribuisce in modo sostanziale alla mitigazione dei cambiamenti climatici o all''adattamento ai cambiamenti climatici e se non arreca un danno significativo a nessun altro obiettivo ambientale (Climate Delegated Act)', 'Regolamento (UE)', '2021/2139', 'Questo regolamento integra il regolamento (UE) 2020/852 per determinare a quali condizioni si possa considerare che un''attività economica contribuisce in modo sostanziale alla mitigazione dei cambiamenti climatici o all''adattamento ai cambiamenti climatici e se non arreca un danno significativo a nessun altro obiettivo ambientale (Climate Delegated Act).', 'I regolamenti sono atti giuridici definiti nell’articolo 288 del trattato sul funzionamento dell’Unione europea (TFUE). Hanno portata generale, sono vincolanti in tutti i loro elementi e direttamente applicabili negli Stati membri dell’Unione. I regolamenti rientrano nel diritto derivato dell’Unione. Sono adottati dalle istituzioni dell’Unione europea sulla base dei trattati. Un regolamento ha carattere vincolante in tutti i suoi elementi, ha portata generale ed è rivolto a categorie astratte di persone, non a specifici individui. 
 https://eur-lex.europa.eu/IT/legal-content/summary/european-union-regulations.html', 'Climate Delegated Act: Regolamento relativo alla mitigazione e l''adattamento ai cambiamenti climatici', 'Regolamento (UE) - 2021/2139', 'Regolamento (UE) 2021/2139', NULL);

-- Record 318/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (461, 'Quadro per le politiche dell''energia e del clima 2030 (UE)', 'Strategia (UE)', 'Politiche dell''energia e del clima 2030', 'Il Quadro europeo per il clima e l''energia al 2030 è una strategia dell''Unione Europea (UE) che stabilisce obiettivi ambiziosi per ridurre le emissioni di gas serra e promuovere fonti energetiche rinnovabili entro il 2030', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Quadro per le politiche dell''energia e del clima 2030 dell''UE', 'Strategia (UE) - Politiche dell''energia e del clima 2030', 'Strategia (UE) Politiche dell''energia e del clima 2030', NULL);

-- Record 319/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (461, 'Direttiva (UE) 2018/2001 del Parlamento europeo e del Consiglio, dell''11 dicembre 2018, sulla promozione dell''uso dell''energia da fonti rinnovabili', 'Direttiva (UE)', '2018/2001', 'La direttiva stabilisce un sistema comune per promuovere l’energia da fonti rinnovabili nei diversi settori e punta ad aumentare l’uso di energia da fonti rinnovabili per combattere i cambiamenti climatici, proteggere l’ambiente e ridurre la dipendenza energetica.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva sulla promozione dell''uso dell''energia da fonti rinnovabili', 'Direttiva (UE) - 2018/2001', 'Direttiva (UE) 2018/2001', NULL);

-- Record 320/2644
INSERT INTO esg_impact.form_legal_bases
