INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (462, 'Regolamento Delegato (UE) 2019/331 della Commissione del 19 dicembre 2018 che stabilisce norme transitorie per l''insieme dell''Unione ai fini dell''armonizzazione delle procedure di assegnazione gratuita delle quote di emissioni ai sensi dell''articolo 10 bis della direttiva 2003/87/CE del Parlamento europeo e del Consiglio', 'Regolamento (UE)', '2019/331', 'Regolamento per l''armonizzazione delle procedure di assegnazione gratuita delle quote di emissioni ai sensi dell''articolo 10 bis della direttiva 2003/87/CE del Parlamento europeo e del Consiglio', 'I regolamenti sono atti giuridici definiti nell’articolo 288 del trattato sul funzionamento dell’Unione europea (TFUE). Hanno portata generale, sono vincolanti in tutti i loro elementi e direttamente applicabili negli Stati membri dell’Unione. I regolamenti rientrano nel diritto derivato dell’Unione. Sono adottati dalle istituzioni dell’Unione europea sulla base dei trattati. Un regolamento ha carattere vincolante in tutti i suoi elementi, ha portata generale ed è rivolto a categorie astratte di persone, non a specifici individui. 
 https://eur-lex.europa.eu/IT/legal-content/summary/european-union-regulations.html', 'Regolamento Delegato (UE) 2019/331', 'Regolamento (UE) - 2019/331', 'Regolamento (UE) 2019/331', NULL);

-- Record 340/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (462, 'Protocollo di Kyoto della Convenzione Quadro delle Nazioni Unite sui Cambiamenti Climatici', 'Protocollo (Int.)', 'Cambiamenti Climatici (Protocollo Kyoto)', 'Il protocollo di Kyoto, che fa seguito alla convenzione quadro delle Nazioni Unite sui cambiamenti climatici, è uno dei più importanti strumenti giuridici internazionali volti a combattere i cambiamenti climatici. Esso contiene gli impegni dei paesi industrializzati a ridurre le emissioni di alcuni gas ad effetto serra, responsabili del riscaldamento del pianeta.', 'Un protocollo è un tipo di accordo formale tra Stati o altre entità sovrane che integra, modifica o specifica ulteriormente un trattato esistente. I protocolli sono strumenti giuridici che possono avere diverse funzioni e scopi, tra cui l''espansione di disposizioni di un trattato, la creazione di nuovi obblighi o la regolamentazione di questioni specifiche non completamente coperte dal trattato originale.', 'Quadro delle Nazioni Unite sui Cambiamenti Climatici', 'Protocollo - Cambiamenti Climatici (Protocollo Kyoto)', 'Protocollo Kyoto', 'Convenzione Quadro delle Nazioni Unite sui Cambiamenti Climatici');

-- Record 341/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (462, 'Accordo di Parigi sui cambiamenti climatici', 'Trattato internazionale', 'Accordo di Parigi sui Cambiamenti climatici', 'L''accordo di Parigi è un trattato internazionale che presenta un piano d''azione per limitare il riscaldamento globale. L’accordo di Parigi, firmato da 194 paesi e dall’UE, mira a limitare il riscaldamento globale al di sotto di 2°C e a proseguire gli sforzi per circoscriverlo a 1,5°C al fine di evitare le conseguenze catastrofiche del cambiamento climatico.', 'L''accordo internazionale può essere considerato come un contratto stipulato tra due o più Stati, diretto a regolare una determinata sfera di rapporti tra i contraenti. La sua formazione è regolata da una serie di norme consuetudinarie e convenzionali che rientrano nel diritto pubblico internazionale.', 'Accordo sui cambiamenti climatici', 'Trattato internazionale - Accordo di Parigi sui Cambiamenti climatici', 'Accordo Parigi (2015)', 'Accordo che mira a limitare il riscaldamento globale al di sotto di 2°C');

