INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (463, 'GRI 302 - Energia (2016)', 'GRI', '302', 'Lo standard GRI 302 riguarda il consumo di energia da parte di un''organizzazione, che può avvenire in varie forme (combustibili, elettricità, riscaldamento, raffreddamento, vapore) e provenire da fonti rinnovabili o non rinnovabili. Il consumo di energia può anche derivare da attività correlate alle operazioni dell''organizzazione, come l''uso dei prodotti da parte dei consumatori o il loro trattamento a fine vita.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Energia (2016)', 'GRI - 302 (2016)', 'GRI 302-1 (a) (2016)', 'L’organizzazione deve rendicontare le seguenti informazioni: (a) Consumo totale di combustibili nell’organizzazione derivanti da fonti di energia non rinnovabile, in joule o multipli, e includendo i tipi di combustibili utilizzati.');

-- Record 354/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (463, 'ESRS E1- Cambiamento climatico', 'ESRS', 'E1', 'Questo Standard copre i requisiti di Informativa GRI  relativi ai seguenti aspetti della sostenibilità: "Mitigazione del cambiamento climatico" e "Adattamento al cambiamento climatico". Copre anche le questioni energetiche, nella misura in cui sono rilevanti per il cambiamento climatico.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Cambiamento climatico', 'ESRS - E1', 'ESRS (E1) E1-5 - 38c', 'Consumo e mix energetico');

-- Record 355/2644
INSERT INTO esg_impact.form_legal_bases
