INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (463, 'EU - Strategia a lungo termine per la neutralità climatica al 2050', 'Strategia (UE)', 'Neutralità climatica al 2050', 'Strategia con l''obiettivo di raggiungere un''Unione Europea climaticamente neutrale entro il 2050, in linea con gli obiettivi dell''Accordo di Parigi e alla luce delle più recenti conoscenze scientifiche disponibili e della necessità di intensificare l''azione climatica globale.', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Strategia a lungo termine per la neutralità climatica al 2050', 'Strategia (UE) - Neutralità climatica al 2050', 'Strategia (UE) Neutralità climatica al 2050', 'Strategia a lungo termine per la neutralità climatica al 2050');

