INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (465, 'Strategia per l''idrogeno per un''Europa climaticamente neutra', 'Strategia (UE)', 'L''idrogeno per un''Europa climaticamente neutra', 'Strategia con l''obiettivo di rendere l''idrogeno pulito una soluzione praticabile per decarbonizzare diversi settori, con l''installazione nell''UE di almeno 6 GW di elettrolizzatori per l''idrogeno rinnovabile entro il 2024 e 40 GW entro il 2030.', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Strategia per l''idrogeno per un''Europa climaticamente neutra', 'Strategia (UE) - L''idrogeno per un''Europa climaticamente neutra', 'Strategia (UE) idrogeno', NULL);

