INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (465, 'Protocollo di Kyoto della Convenzione Quadro delle Nazioni Unite sui Cambiamenti Climatici', 'Protocollo (Int.)', 'Cambiamenti Climatici (Protocollo Kyoto)', 'Il protocollo di Kyoto, che fa seguito alla convenzione quadro delle Nazioni Unite sui cambiamenti climatici, è uno dei più importanti strumenti giuridici internazionali volti a combattere i cambiamenti climatici. Esso contiene gli impegni dei paesi industrializzati a ridurre le emissioni di alcuni gas ad effetto serra, responsabili del riscaldamento del pianeta.', 'Un protocollo è un tipo di accordo formale tra Stati o altre entità sovrane che integra, modifica o specifica ulteriormente un trattato esistente. I protocolli sono strumenti giuridici che possono avere diverse funzioni e scopi, tra cui l''espansione di disposizioni di un trattato, la creazione di nuovi obblighi o la regolamentazione di questioni specifiche non completamente coperte dal trattato originale.', 'Quadro delle Nazioni Unite sui Cambiamenti Climatici', 'Protocollo - Cambiamenti Climatici (Protocollo Kyoto)', 'Protocollo Kyoto', 'Convenzione Quadro delle Nazioni Unite sui Cambiamenti Climatici');

-- Record 388/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (465, 'Accordo di Parigi sui cambiamenti climatici', 'Trattato internazionale', 'Accordo di Parigi sui Cambiamenti climatici', 'L''accordo di Parigi è un trattato internazionale che presenta un piano d''azione per limitare il riscaldamento globale. L’accordo di Parigi, firmato da 194 paesi e dall’UE, mira a limitare il riscaldamento globale al di sotto di 2°C e a proseguire gli sforzi per circoscriverlo a 1,5°C al fine di evitare le conseguenze catastrofiche del cambiamento climatico.', 'L''accordo internazionale può essere considerato come un contratto stipulato tra due o più Stati, diretto a regolare una determinata sfera di rapporti tra i contraenti. La sua formazione è regolata da una serie di norme consuetudinarie e convenzionali che rientrano nel diritto pubblico internazionale.', 'Accordo sui cambiamenti climatici', 'Trattato internazionale - Accordo di Parigi sui Cambiamenti climatici', 'Accordo Parigi (2015)', 'Accordo che mira a limitare il riscaldamento globale al di sotto di 2°C');

-- Record 389/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (465, 'Strategia Nazionale Adattamento Cambiamenti Climatici (2015)', 'Strategia Nazionale (IT)', 'Adattamento Cambiamenti Climatici (2015)', 'La Strategia Nazionale di Adattamento ai Cambiamenti Climatici ha l''obiettivo di elaborare una visione nazionale sui percorsi comuni da intraprendere per far fronte ai cambiamenti climatici, contrastando e attenuando i loro impatti. La strategia mira a preparare e proteggere l''Italia dagli effetti dei cambiamenti climatici attraverso una serie di misure integrate e coordinate per aumentare la resilienza e la capacità di adattamento del paese.', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia per l''adattamento Cambiamenti Climatici', 'Strategia Nazionale - Adattamento Cambiamenti Climatici (2015)', 'SNAC (2015)', 'Strategia Nazionale Adattamento Cambiamenti Climatici');

