INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (467, 'C111 - Convenzione sulla discriminazione (impiego e professione), 1958 - ILO', 'ILO', 'Convenzione 111', 'La Convenzione 111 dell''Organizzazione Internazionale del Lavoro (ILO), adottata nel 1958, è conosciuta come la Convenzione sulla discriminazione (impiego e professione). Questa convenzione mira a combattere la discriminazione nel campo dell''occupazione e della professione e a promuovere la parità di opportunità e di trattamento.', 'L''Organizzazione Internazionale del Lavoro (OIL), fondata nel 1919, è un''agenzia specializzata delle Nazioni Unite che promuove i diritti nel lavoro, opportunità di occupazione dignitosa, protezione sociale e il dialogo sulle questioni lavorative. L’OIL definisce standard internazionali attraverso convenzioni e raccomandazioni, contribuendo a sostenere la giustizia sociale e i diritti umani nel mondo del lavoro. Un elemento distintivo è il suo approccio tripartito, che coinvolge governi, datori di lavoro e lavoratori per sviluppare politiche e programmi efficaci.

Gli standard internazionali del lavoro, elaborati dai costituenti dell''OIL, stabiliscono principi e diritti fondamentali nel lavoro. Questi strumenti si suddividono in: -Convenzioni e Protocolli, trattati internazionali legalmente vincolanti che possono essere ratificati dagli Stati membri; -Raccomandazioni, che offrono linee guida non vincolanti per orientare le politiche nazionali.
Per maggiore informazione vedere il sito: https://www.ilo.org/international-labour-standards', 'Sulla discriminazione (impiego e professione)', 'Convenzione 111 - ILO', 'ILO- C111', 'Convenzione 111, ILO – mira a combattere la discriminazione nel campo dell''occupazione e della professione e a promuovere la parità di opportunità e di trattamento');

-- Record 410/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (467, 'C158 - Convenzione sul licenziamento, 1982, ILO', 'ILO', 'Convenzione 158', 'La Convenzione 158 dell’Organizzazione Internazionale del Lavoro (ILO), adottata nel 1982, stabilisce norme fondamentali sulla protezione contro i licenziamenti e mira a garantire che i lavoratori non siano licenziati arbitrariamente o senza una giusta causa. Si tratta di uno strumento chiave per promuovere la giustizia e la stabilità nelle relazioni di lavoro.', 'L''Organizzazione Internazionale del Lavoro (OIL), fondata nel 1919, è un''agenzia specializzata delle Nazioni Unite che promuove i diritti nel lavoro, opportunità di occupazione dignitosa, protezione sociale e il dialogo sulle questioni lavorative. L’OIL definisce standard internazionali attraverso convenzioni e raccomandazioni, contribuendo a sostenere la giustizia sociale e i diritti umani nel mondo del lavoro. Un elemento distintivo è il suo approccio tripartito, che coinvolge governi, datori di lavoro e lavoratori per sviluppare politiche e programmi efficaci.

Gli standard internazionali del lavoro, elaborati dai costituenti dell''OIL, stabiliscono principi e diritti fondamentali nel lavoro. Questi strumenti si suddividono in: -Convenzioni e Protocolli, trattati internazionali legalmente vincolanti che possono essere ratificati dagli Stati membri; -Raccomandazioni, che offrono linee guida non vincolanti per orientare le politiche nazionali.
Per maggiore informazione vedere il sito: https://www.ilo.org/international-labour-standards', 'Sul licenziamento', 'Convenzione 158 - ILO', 'ILO- C158', 'Convenzione 158, ILO – stabilisce norme fondamentali sulla protezione contro i licenziamenti');

-- Record 411/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (468, 'SDGs: Goal 8 Lavoro dignitoso e crescita economica', 'SDGs', 'Goal 8', 'Incentivare una crescita economica duratura, inclusiva e sostenibile, un’occupazione piena e produttiva ed un lavoro dignitoso per tutti', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Lavoro dignitoso e crescita economica', 'SDGs - Goal 8', 'SDG 8.8', 'Proteggere il diritto al lavoro e promuovere un ambiente lavorativo sano e sicuro per tutti i lavoratori, inclusi gli immigrati, in particolare le donne, e i precari');

-- Record 412/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (468, 'ESRS S1- Forza lavoro propria', 'ESRS', 'S1', 'L''obiettivo di questo Standard è specificare i requisiti di divulgazione che consentiranno agli utenti della dichiarazione di sostenibilità di comprendere gli impatti materiali dell''azienda sul proprio personale, nonché i rischi e le opportunità materiali correlati.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Forza lavoro propria', 'ESRS - S1', 'ESRS (S1) S1-1 20', 'Descrizione degli impegni relativi ai diritti umani rilevanti per la propria forza lavoro');

-- Record 413/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (468, 'Global Compact delle Nazioni Unite (UNGC): Principio 1- Diritti Umani', 'UNGC', 'Principio 1', 'Le imprese dovrebbero sostenere e rispettare la protezione dei diritti umani proclamati a livello internazionale', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Diritti Umani', 'UNGC - Principio 1', 'UNGC P.1', 'Le imprese dovrebbero sostenere e rispettare la protezione dei diritti umani proclamati a livello internazionale');

-- Record 414/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (468, 'Global Compact delle Nazioni Unite (UNGC): Principio 3- Lavoro', 'UNGC', 'Principio 3', 'Le imprese dovrebbero sostenere la libertà di associazione e l''effettivo riconoscimento del diritto alla contrattazione collettiva', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Lavoro', 'UNGC - Principio 3', 'UNGC P.3', 'Le imprese dovrebbero sostenere la libertà di associazione e l''effettivo riconoscimento del diritto alla contrattazione collettiva');

-- Record 415/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (468, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 11: La responsabilità di rispettare', 'UNGPs', 'Principio 11', 'Le imprese dovrebbero rispettare i diritti umani. Questo significa che esse dovrebbero evitare di violare i diritti umani di altre persone e affrontare gli impatti negativi sui diritti umani in cui esse siano coinvolte', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'La responsabilità di rispettare', 'UNGPs - Principio 11', 'UNGPs P.11', 'Le imprese dovrebbero rispettare i diritti umani. Questo significa che esse dovrebbero evitare di violare i diritti umani di altre persone e affrontare gli impatti negativi sui diritti umani in cui esse siano coinvolte');

-- Record 416/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (468, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 12: I diritti umani internazionalmente riconosciuti', 'UNGPs', 'Principio 12', 'La responsabilità delle imprese di rispettare i diritti umani si riferisce ai diritti umani internazionalmente riconosciuti – intesi, come minimo, quelli espressi nella Carta Internazionale dei Diritti Umani e i principi concernenti i diritti fondamentali enunciati nella Dichiarazione sui Principi e i Diritti Fondamentali nel Lavoro dell’Organizzazione Internazionale del Lavoro', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'I diritti umani internazionalmente riconosciuti', 'UNGPs - Principio 12', 'UNGPs P.12', 'La responsabilità delle imprese di rispettare i diritti umani si riferisce ai diritti umani internazionalmente riconosciuti – intesi, come minimo, quelli espressi nella Carta Internazionale dei Diritti Umani e i principi concernenti i diritti fondamentali enunciati nella Dichiarazione sui Principi e i Diritti Fondamentali nel Lavoro dell’Organizzazione Internazionale del Lavoro');

