INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'SDGs: Goal 10 Ridurre le disuguaglianze', 'SDGs', 'Goal 10', 'Ridurre l’ineguaglianza all’interno di e fra le Nazioni', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Ridurre le disuguaglianze', 'SDGs - Goal 10', 'SDG 10.3', 'Assicurare pari opportunità e ridurre le disuguaglianze nei risultati, anche eliminando leggi, politiche e pratiche discriminatorie e promuovendo legislazioni, politiche e azioni appropriate a tale proposito');

-- Record 427/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'GRI 401- Occupazione (2016)', 'GRI', '401', 'Questo Standard tratta il tema dell''occupazione. Riguarda l''approccio di un''organizzazione all''occupazione o alla creazione di posti di lavoro, l''approccio all''assunzione o all''ingaggio e alla fidelizzazione del personale e alle relative prassi, nonché alle condizioni di lavoro offerte. Lo Standard riguarda anche le condizioni di impiego e di lavoro della catena di fornitura di un''organizzazione.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Occupazione (2016)', 'GRI - 401 (2016)', 'GRI 401-1 (a) (2016)', 'L’organizzazione deve rendicontare le seguenti informazioni: (a) Numero totale e tasso di nuovi dipendenti assunti durante il periodo di rendicontazione, suddivisi per età, genere e area geografica.');

-- Record 428/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'ESRS S1- Forza lavoro propria', 'ESRS', 'S1', 'L''obiettivo di questo Standard è specificare i requisiti di divulgazione che consentiranno agli utenti della dichiarazione di sostenibilità di comprendere gli impatti materiali dell''azienda sul proprio personale, nonché i rischi e le opportunità materiali correlati.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Forza lavoro propria', 'ESRS - S1', 'ESRS (S1) S6 - 50a', 'Caratteristiche dei dipendenti dell''impresa - numero di dipendenti per genere');

-- Record 429/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'GRI 405- Diversità e pari opportunità (2016)', 'GRI', '405', 'Questo Standard tratta il tema dell’approccio dell’organizzazione riguardo alla diversità e alle pari opportunità sul lavoro. Contiene informative per le organizzazioni che devono rendicontare informazioni sui loro impatti relativamente a diversità e pari opportunità sul lavoro, e su come li gestiscono.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Diversità e pari opportunità (2016)', 'GRI - 405 (2016)', 'GRI 405-1 (b) (2016)', 'L’organizzazione deve rendicontare le seguenti informazioni: (b) Percentuale di dipendenti per categorie appartenenti alle seguenti categorie di diversità');

-- Record 430/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'WEF- Metriche del Capitalismo degli Stakeholder 2020 P3-1 (Dignità e uguaglianza)', 'WEF', '4P: Metrica P3-1', 'Questo tema promuove opportunità eque per tutti i dipendenti, indipendentemente da genere, razza, età, etnia, abilità o orientamento sessuale, in un ambiente inclusivo e rispettoso. Favorendo diversità e pari opportunità, le aziende possono integrare gruppi sotto-rappresentati, riflettere meglio la società e ampliare il bacino di talenti disponibili.', 'Il World Economic Forum è l''Organizzazione Internazionale per la Cooperazione Pubblico-Privata. L''WEF insieme ad altre istituzioni ha sviluppato un insieme di metriche comuni per la rendicontazione della creazione di valore sostenibile (21 metriche principali e 34 metriche e divulgazioni ampliate) raggruprate in 4 pilastri principali (Principle of Governance, Planet, Persone and Prosperity). Per maggiore informazioni sulle 4 Pilastri (Metriche) di sostenibilità del WEF andare su: https://www.weforum.org/stakeholdercapitalism/our-metrics/', '1- Dignità e uguaglianza', 'WEF (MSC2024) 4P- P3-1', 'WEF (MSC2024) 4P- P3-1 CM 1', 'Diversità e Inclusione (%)');

-- Record 431/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'Global Compact delle Nazioni Unite (UNGC): Principio 6- Lavoro', 'UNGC', 'Principio 6', 'Le imprese dovrebbero sostenere l''eliminazione della discriminazione in materia di lavoro e occupazione.', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Lavoro', 'UNGC - Principio 6', 'UNGC P.6', 'Le imprese dovrebbero sostenere l''eliminazione della discriminazione in materia di lavoro e occupazione.');

-- Record 432/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'Strategia Nazionale per la parità di genere 2021-2026: Target Lavoro', 'Strategia Nazionale (IT)', 'Parità di genere - Target: Lavoro', 'Con l’obiettivo di ridurre il divario occupazionale di genere, aumentando la partecipazione femminile nel mondo del lavoro, con particolare attenzione all’inclusione dei genitori donna e all’attività di imprenditoria femminile, vengono misurati: 
 -Incremento del tasso di occupazione femminile; l’ulteriore aggravio in termini di differenza di tasso di occupazione femminile per le donne con figli (rispetto alle donne senza figli); la percentuale di imprese “femminili”36 rispetto al totale delle imprese attive.', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia Nazionale per la parità di genere 2021-2026', 'Strategia Nazionale - Parità di genere - Target: Lavoro', 'Strategia (IT) Parità di genere- Target: Lavoro', 'Strategia Nazionale per la parità di genere: Target Lavoro');

-- Record 433/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'Legge 162/2021 - Modifiche al codice di cui al Decreto Legislativo 11 aprile 2006, n. 198, e altre disposizioni in materia di pari opportunita'' tra uomo e donna in ambito lavorativo', 'Legge (IT)', '162/2021', 'La legge 162/2021 ha introdotto nuove regole per promuovere la parità tra uomini e donne sul lavoro e introduce anche la certificazione di genere, che le aziende possono ottenere per attestare pari retribuzione e carriera tra generi, ottenendo esenzioni contributive fino all''1% sui salari e fino a 50.000 euro annui.', 'Il termine legge viene usato nel diritto con una molteplicità di significati, si distingue in:
la norma giuridica; l’atto normativo (legge in senso materiale); l’atto giuridico attraverso il quale il potere legislativo esercita la sua funzione (legge in senso formale); l’insieme delle norme giuridiche che costituiscono l’ordinamento giuridico, ossia il diritto oggettivo. Per maggiori informazioni accedere al link:
https://www.governo.it/it/costituzione-italiana/principi-fondamentali/2839', 'Modifiche al codice di cui al Decreto Legislativo 11 aprile 2006, n. 198, e altre disposizioni in materia di pari opportunita'' tra uomo e donna in ambito lavorativo', 'Legge (IT) - 162/2021', 'Legge 162/2021', NULL);

-- Record 434/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'Decreto Legislativo 5/2010 - Pari opportunità e parità di trattamento fra uomini e donne in materia di occupazione e impiego', 'Decreto (IT)', '5/2010', 'Questo Decreto si occupa della parità di genere e delle pari opportunità nel contesto lavorativo, specificamente per quanto riguarda il settore pubblico e l''impiego pubblico. Questa direttiva europea mira a promuovere la parità di trattamento tra uomini e donne in materia di occupazione e condizioni di lavoro.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Pari opportunità e parità di trattamento fra uomini e donne in materia di occupazione e impiego', 'Decreto Legislativo - 5/2010', 'D.Lgs. 5/2010', NULL);

-- Record 435/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'Direttiva (UE) 2022/2464 del Parlamento europeo e del Consiglio del 14 dicembre 2022 che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE)', '2022/2464', 'La CSRD o Corporate Sustainability Reporting Directive (Direttiva 2022/2464) è la direttiva europea che mira a migliorare la trasparenza e la qualità delle informazioni sulla sostenibilità fornite dalle aziende. La CSRD sostituisce e amplia la Non-Financial Reporting Directive (NFRD), aumentando le esigenze di rendicontazione per le imprese riguardo alle loro attività sostenibili. I punti chiavi della CSRD sono: -L''ampiezza del campo di applicazione rispetto alla NFRD rispetto alle aziende che devono rendicontare, - le informazioni da rendicontare in termini ESG e di sostenibilità; - lo standard di rendicontazione (gli ESRS); - L''audit e certificazione; e la digitalizzazione delle informazioni. 
 
 L''obiettivo principale della CSRD è migliorare la trasparenza e responsabilizzare le aziende verso pratiche più sostenibili, aiutando investitori, consumatori, e altre parti interessate a prendere decisioni informate basate su dati di sostenibilità accurati e completi.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE) - 2022/2464', 'CSRD', 'Rendicontazione societaria di sostenibilità (CSRD)');

-- Record 436/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'Costituzione Italiana- Articolo 3: Principio dell''eguaglianza formale e sostanziale', 'Costituzione Italiana', 'Articolo 3', 'L''articolo 3 della Costituzione Italiana stabilisce che: "Tutti i cittadini hanno pari dignità sociale e sono eguali davanti alla legge, senza distinzione di sesso, di razza, di lingua, di religione, di opinioni politiche, di condizioni personali e sociali. È compito della Repubblica rimuovere gli ostacoli di ordine economico e sociale, che, limitando di fatto la libertà e l’eguaglianza dei cittadini, impediscono il pieno sviluppo della persona umana e l’effettiva partecipazione di tutti i lavoratori all’organizzazione politica, economica e sociale del Paese".', 'La Costituzione Italiana è la legge fondamentale della Repubblica Italiana. Essa stabilisce i principi fondamentali su cui si basa l''ordinamento dello Stato italiano, definendo i diritti e i doveri dei cittadini, l''organizzazione e i poteri delle istituzioni statali (Parlamento, Governo e Magistratura), nonché i principi economici e sociali dello Stato. La Costituzione è la norma suprema, alla quale devono conformarsi tutte le altre leggi e normative del Paese.', 'Principio dell''eguaglianza formale e sostanziale', 'Costituzione Italiana - Articolo 3', 'Art. 3 Cost.', 'Tutti i cittadini hanno pari dignità sociale e sono eguali davanti alla legge, senza distinzione di sesso, di razza, di lingua, di religione, di opinioni politiche, di condizioni personali e sociali. È compito della Repubblica rimuovere gli ostacoli di ordine economico e sociale, che, limitando di fatto la libertà e l’eguaglianza dei cittadini, impediscono il pieno sviluppo della persona umana e l’effettiva partecipazione di tutti i lavoratori all’organizzazione politica, economica e sociale del Paese.');

-- Record 437/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'ISO 30415: Gestione delle risorse umane - Diversità e inclusione', 'ISO', '30415', 'L''ISO 30415 è lo standard che fornisce linee guida sulla diversità e inclusione per le organizzazioni, compreso il loro organo di governo, i leader, la forza lavoro e i rappresentanti riconosciuti, e altri portatori di interessi.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Gestione delle risorse umane - Diversità e inclusione', 'ISO - 30415', 'ISO 30415', 'Gestione delle risorse umane - Diversità e inclusione');

-- Record 438/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'C100 - Convenzione sull’uguaglianza di retribuzione, 1951 - ILO', 'ILO', 'Convenzione 100', 'La Convenzione 100 dell''Organizzazione Internazionale del Lavoro (ILO), nota anche come Convenzione sull''uguaglianza di retribuzione, è uno strumento internazionale adottato nel 1951 che mira a garantire la parità di retribuzione tra uomini e donne per un lavoro di pari valore.', 'L''Organizzazione Internazionale del Lavoro (OIL), fondata nel 1919, è un''agenzia specializzata delle Nazioni Unite che promuove i diritti nel lavoro, opportunità di occupazione dignitosa, protezione sociale e il dialogo sulle questioni lavorative. L’OIL definisce standard internazionali attraverso convenzioni e raccomandazioni, contribuendo a sostenere la giustizia sociale e i diritti umani nel mondo del lavoro. Un elemento distintivo è il suo approccio tripartito, che coinvolge governi, datori di lavoro e lavoratori per sviluppare politiche e programmi efficaci.

Gli standard internazionali del lavoro, elaborati dai costituenti dell''OIL, stabiliscono principi e diritti fondamentali nel lavoro. Questi strumenti si suddividono in: -Convenzioni e Protocolli, trattati internazionali legalmente vincolanti che possono essere ratificati dagli Stati membri; -Raccomandazioni, che offrono linee guida non vincolanti per orientare le politiche nazionali.
Per maggiore informazione vedere il sito: https://www.ilo.org/international-labour-standards', 'Sull’uguaglianza di retribuzione -ILO', 'Convenzione 100 - ILO', 'ILO- C100', 'Convenzione 100, ILO – mira a garantire la parità di retribuzione tra uomini e donne per un lavoro di pari valore');

-- Record 439/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'C111 - Convenzione sulla discriminazione (impiego e professione), 1958 - ILO', 'ILO', 'Convenzione 111', 'La Convenzione 111 dell''Organizzazione Internazionale del Lavoro (ILO), adottata nel 1958, è conosciuta come la Convenzione sulla discriminazione (impiego e professione). Questa convenzione mira a combattere la discriminazione nel campo dell''occupazione e della professione e a promuovere la parità di opportunità e di trattamento.', 'L''Organizzazione Internazionale del Lavoro (OIL), fondata nel 1919, è un''agenzia specializzata delle Nazioni Unite che promuove i diritti nel lavoro, opportunità di occupazione dignitosa, protezione sociale e il dialogo sulle questioni lavorative. L’OIL definisce standard internazionali attraverso convenzioni e raccomandazioni, contribuendo a sostenere la giustizia sociale e i diritti umani nel mondo del lavoro. Un elemento distintivo è il suo approccio tripartito, che coinvolge governi, datori di lavoro e lavoratori per sviluppare politiche e programmi efficaci.

Gli standard internazionali del lavoro, elaborati dai costituenti dell''OIL, stabiliscono principi e diritti fondamentali nel lavoro. Questi strumenti si suddividono in: -Convenzioni e Protocolli, trattati internazionali legalmente vincolanti che possono essere ratificati dagli Stati membri; -Raccomandazioni, che offrono linee guida non vincolanti per orientare le politiche nazionali.
Per maggiore informazione vedere il sito: https://www.ilo.org/international-labour-standards', 'Sulla discriminazione (impiego e professione)', 'Convenzione 111 - ILO', 'ILO- C111', 'Convenzione 111, ILO – mira a combattere la discriminazione nel campo dell''occupazione e della professione e a promuovere la parità di opportunità e di trattamento');

-- Record 440/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (469, 'Direttiva 2006/54/CE del Parlamento europeo e del Consiglio, del 5 luglio 2006, riguardante l''attuazione del principio delle pari opportunità e della parità di trattamento fra uomini e donne in materia di occupazione e impiego', 'Direttiva (UE)', '2006/54/CE', 'La direttiva ha lo scopo di assicurare l''attuazione del principio delle pari opportunità e della parità di trattamento fra uomini e donne in materia di occupazione e impiego.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Attuazione del principio delle pari opportunità e della parità di trattamento fra uomini e donne in materia di occupazione e impiego', 'Direttiva (UE) - 2006/54/CE', 'Direttiva (UE) 2006/54/CE', NULL);

