INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (471, 'ESRS S1- Forza lavoro propria', 'ESRS', 'S1', 'L''obiettivo di questo Standard è specificare i requisiti di divulgazione che consentiranno agli utenti della dichiarazione di sostenibilità di comprendere gli impatti materiali dell''azienda sul proprio personale, nonché i rischi e le opportunità materiali correlati.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Forza lavoro propria', 'ESRS - S1', 'ESRS (S1) S1-12 79', 'Percentuale di persone con disabilità tra i dipendenti, soggetta a restrizioni legali sulla raccolta dei dati');

-- Record 457/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (471, 'GRI 405- Diversità e pari opportunità (2016)', 'GRI', '405', 'Questo Standard tratta il tema dell’approccio dell’organizzazione riguardo alla diversità e alle pari opportunità sul lavoro. Contiene informative per le organizzazioni che devono rendicontare informazioni sui loro impatti relativamente a diversità e pari opportunità sul lavoro, e su come li gestiscono.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Diversità e pari opportunità (2016)', 'GRI - 405 (2016)', 'GRI 405-1 (b-iii) (2016)', '(Diversità negli organi di governance e tra i dipendenti)
L’organizzazione deve rendicontare le seguenti informazioni: b.iii. altri indicatori di diversità, se rilevanti (come minoranze o categorie vulnerabili).');

-- Record 458/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (471, 'WEF- Metriche del Capitalismo degli Stakeholder 2020 P3-1 (Dignità e uguaglianza)', 'WEF', '4P: Metrica P3-1', 'Questo tema promuove opportunità eque per tutti i dipendenti, indipendentemente da genere, razza, età, etnia, abilità o orientamento sessuale, in un ambiente inclusivo e rispettoso. Favorendo diversità e pari opportunità, le aziende possono integrare gruppi sotto-rappresentati, riflettere meglio la società e ampliare il bacino di talenti disponibili.', 'Il World Economic Forum è l''Organizzazione Internazionale per la Cooperazione Pubblico-Privata. L''WEF insieme ad altre istituzioni ha sviluppato un insieme di metriche comuni per la rendicontazione della creazione di valore sostenibile (21 metriche principali e 34 metriche e divulgazioni ampliate) raggruprate in 4 pilastri principali (Principle of Governance, Planet, Persone and Prosperity). Per maggiore informazioni sulle 4 Pilastri (Metriche) di sostenibilità del WEF andare su: https://www.weforum.org/stakeholdercapitalism/our-metrics/', '1- Dignità e uguaglianza', 'WEF (MSC2024) 4P- P3-1', 'WEF (MSC2024) 4P- P3-1 CM 1', 'Diversità e Inclusione (%)');

-- Record 459/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (471, 'Global Compact delle Nazioni Unite (UNGC): Principio 6- Lavoro', 'UNGC', 'Principio 6', 'Le imprese dovrebbero sostenere l''eliminazione della discriminazione in materia di lavoro e occupazione.', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Lavoro', 'UNGC - Principio 6', 'UNGC P.6', 'Le imprese dovrebbero sostenere l''eliminazione della discriminazione in materia di lavoro e occupazione.');

-- Record 460/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (471, 'Legge 12 marzo 1999, n. 68 Norme per il diritto al lavoro dei disabili', 'Legge (IT)', '68/1999', 'La legge 68/1999 ha come finalità la promozione dell''inserimento e della integrazione lavorativa delle persone disabili nel mondo del lavoro attraverso servizi di sostegno e di collocamento mirato', 'Il termine legge viene usato nel diritto con una molteplicità di significati, si distingue in:
la norma giuridica; l’atto normativo (legge in senso materiale); l’atto giuridico attraverso il quale il potere legislativo esercita la sua funzione (legge in senso formale); l’insieme delle norme giuridiche che costituiscono l’ordinamento giuridico, ossia il diritto oggettivo. Per maggiori informazioni accedere al link:
https://www.governo.it/it/costituzione-italiana/principi-fondamentali/2839', 'Norme per il diritto al lavoro dei disabili', 'Legge (IT) - 68/1999', 'Legge 68/1999', 'Norme per il diritto al lavoro dei disabili');

-- Record 461/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (471, 'Decreto Legislativo 5/2010 - Pari opportunità e parità di trattamento fra uomini e donne in materia di occupazione e impiego', 'Decreto (IT)', '5/2010', 'Questo Decreto si occupa della parità di genere e delle pari opportunità nel contesto lavorativo, specificamente per quanto riguarda il settore pubblico e l''impiego pubblico. Questa direttiva europea mira a promuovere la parità di trattamento tra uomini e donne in materia di occupazione e condizioni di lavoro.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Pari opportunità e parità di trattamento fra uomini e donne in materia di occupazione e impiego', 'Decreto Legislativo - 5/2010', 'D.Lgs. 5/2010', NULL);

-- Record 462/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (471, 'Direttiva (UE) 2022/2464 del Parlamento europeo e del Consiglio del 14 dicembre 2022 che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE)', '2022/2464', 'La CSRD o Corporate Sustainability Reporting Directive (Direttiva 2022/2464) è la direttiva europea che mira a migliorare la trasparenza e la qualità delle informazioni sulla sostenibilità fornite dalle aziende. La CSRD sostituisce e amplia la Non-Financial Reporting Directive (NFRD), aumentando le esigenze di rendicontazione per le imprese riguardo alle loro attività sostenibili. I punti chiavi della CSRD sono: -L''ampiezza del campo di applicazione rispetto alla NFRD rispetto alle aziende che devono rendicontare, - le informazioni da rendicontare in termini ESG e di sostenibilità; - lo standard di rendicontazione (gli ESRS); - L''audit e certificazione; e la digitalizzazione delle informazioni. 
 
 L''obiettivo principale della CSRD è migliorare la trasparenza e responsabilizzare le aziende verso pratiche più sostenibili, aiutando investitori, consumatori, e altre parti interessate a prendere decisioni informate basate su dati di sostenibilità accurati e completi.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE) - 2022/2464', 'CSRD', 'Rendicontazione societaria di sostenibilità (CSRD)');

-- Record 463/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (471, 'Costituzione Italiana- Articolo 3: Principio dell''eguaglianza formale e sostanziale', 'Costituzione Italiana', 'Articolo 3', 'L''articolo 3 della Costituzione Italiana stabilisce che: "Tutti i cittadini hanno pari dignità sociale e sono eguali davanti alla legge, senza distinzione di sesso, di razza, di lingua, di religione, di opinioni politiche, di condizioni personali e sociali. È compito della Repubblica rimuovere gli ostacoli di ordine economico e sociale, che, limitando di fatto la libertà e l’eguaglianza dei cittadini, impediscono il pieno sviluppo della persona umana e l’effettiva partecipazione di tutti i lavoratori all’organizzazione politica, economica e sociale del Paese".', 'La Costituzione Italiana è la legge fondamentale della Repubblica Italiana. Essa stabilisce i principi fondamentali su cui si basa l''ordinamento dello Stato italiano, definendo i diritti e i doveri dei cittadini, l''organizzazione e i poteri delle istituzioni statali (Parlamento, Governo e Magistratura), nonché i principi economici e sociali dello Stato. La Costituzione è la norma suprema, alla quale devono conformarsi tutte le altre leggi e normative del Paese.', 'Principio dell''eguaglianza formale e sostanziale', 'Costituzione Italiana - Articolo 3', 'Art. 3 Cost.', 'Tutti i cittadini hanno pari dignità sociale e sono eguali davanti alla legge, senza distinzione di sesso, di razza, di lingua, di religione, di opinioni politiche, di condizioni personali e sociali. È compito della Repubblica rimuovere gli ostacoli di ordine economico e sociale, che, limitando di fatto la libertà e l’eguaglianza dei cittadini, impediscono il pieno sviluppo della persona umana e l’effettiva partecipazione di tutti i lavoratori all’organizzazione politica, economica e sociale del Paese.');

-- Record 464/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (471, 'ISO 30415: Gestione delle risorse umane - Diversità e inclusione', 'ISO', '30415', 'L''ISO 30415 è lo standard che fornisce linee guida sulla diversità e inclusione per le organizzazioni, compreso il loro organo di governo, i leader, la forza lavoro e i rappresentanti riconosciuti, e altri portatori di interessi.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Gestione delle risorse umane - Diversità e inclusione', 'ISO - 30415', 'ISO 30415', 'Gestione delle risorse umane - Diversità e inclusione');

-- Record 465/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (471, 'Direttiva (UE) 2024/1500 del Parlamento europeo e del Consiglio, del 14 maggio 2024, sulle norme riguardanti gli organismi per la parità nel settore della parità di trattamento e delle pari opportunità tra donne e uomini in materia di occupazione e impiego, e che modifica le direttive 2006/54/CE e 2010/41/UE', 'Direttiva (UE)', '2024/1500', 'La Direttiva (UE) 2024/1500 stabilisce norme riguardanti gli organismi per la parità nel settore della parità di trattamento e delle pari opportunità tra donne e uomini in materia di occupazione e impiego. Questa direttiva mira a rafforzare l''efficacia e l''indipendenza degli organismi per la parità, con l''obiettivo di garantire un''applicazione più rigorosa del principio di parità di trattamento derivante dalle direttive precedenti, in particolare la 2006/54/CE e la 2010/41/UE.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Sulle norme riguardanti gli organismi per la parità nel settore della parità di trattamento e delle pari opportunità tra donne e uomini in materia di occupazione e impiego', 'Direttiva (UE) - 2024/1500', 'Direttiva (UE) 2024/1500', NULL);

