INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (472, 'Linee guida dell''OCSE per le imprese multinazionali sulla condotta responsabile delle imprese: Principio 8', 'OECD', 'Linee guida - Principio 8', 'Il principio 8 stabilisce che le imprese dovrebbero: Promuovere fra i lavoratori alle dipendenze delle imprese multinazionali la consapevolezza delle politiche aziendali e l’adesione alle stesse, attraverso una loro appropriata divulgazione, anche tramite programmi di formazione.', 'Le Linee guida dell''OCSE per le imprese multinazionali sono raccomandazioni governative rivolte alle aziende, volte a promuovere una condotta responsabile. L''obiettivo è incentivare contributi positivi al progresso economico, ambientale e sociale, riducendo al contempo gli impatti negativi legati alle operazioni, ai prodotti e ai servizi aziendali. Le Linee guida coprono tutte le principali aree di responsabilità d''impresa, tra cui diritti umani, diritti dei lavoratori, ambiente, corruzione, interessi dei consumatori, trasparenza, scienza e tecnologia, concorrenza e fiscalità.', 'Linee guida per le imprese multinazionali sulla condotta responsabile delle imprese', 'OECD - Linee guida - Principio 8', 'Linee guida OECD P.8', 'Promuovere fra i lavoratori alle dipendenze delle imprese multinazionali la consapevolezza delle politiche aziendali e l’adesione alle stesse, attraverso una loro appropriata divulgazione, anche tramite programmi di formazione.');

