INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (472, 'Decreto Legislativo 9 aprile 2008, n. 81 Attuazione dell''articolo 1 della legge 3 agosto 2007, n. 123, in materia di tutela della salute e della sicurezza nei luoghi di lavoro.', 'Decreto (IT)', '81/2008', 'Il Decreto 81/2008 recepisce le direttive comunitarie in materia di salute e sicurezza sui luoghi di lavoro. Il testo normativo, in particolare, prevede un modello partecipativo della valutazione dei rischi finalizzato a programmare la prevenzione contro gli infortuni e altri danni alla salute del lavoratore.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Tutela della salute e della sicurezza nei luoghi di lavoro.', 'Decreto Legislativo - 81/2008', 'D.Lgs. 81/2008', NULL);

-- Record 482/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (472, 'ISO 45001: Sistemi di gestione per la salute e sicurezza sul lavoro', 'ISO', '45001', 'ISO 45001 è uno standard globale che definisce i requisiti per gestire la salute e la sicurezza sul lavoro. Aiuta le organizzazioni a gestire i rischi e migliorare le loro performance in quest''area.
 
 L''ISO 45001 stabilisce i criteri per una politica di salute e sicurezza sul lavoro, obiettivi, pianificazione, implementazione, operazioni, audit e revisione. Gli elementi principali comprendono l''engagement della leadership, la partecipazione dei lavoratori, l''identificazione dei rischi, la conformità legale, la pianificazione delle emergenze, l''indagine sugli incidenti e il miglioramento continuo.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Sistemi di gestione per la salute e sicurezza sul lavoro', 'ISO - 45001', 'ISO 45001', 'Sistemi di gestione per la salute e sicurezza sul lavoro');

-- Record 483/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (472, 'Decreto Legislativo 198/2006 - Codice delle pari opportunità tra uomo e donna, a norma dell''articolo 6 della legge 28 novembre 2005, n. 246.', 'Decreto (IT)', '198/2006', 'Il decreto stabilisce le misure volte ad eliminare ogni discriminazione basata sul sesso, che abbia come conseguenza o come scopo di compromettere o di impedire il riconoscimento, il godimento o l''esercizio dei diritti umani e delle libertà fondamentali in campo politico, economico, sociale, culturale e civile o in ogni altro campo.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Codice delle pari opportunità tra uomo e donna', 'Decreto Legislativo - 198/2006', 'D.Lgs. 198/2006', NULL);

