INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (475, 'GRI 2- Informativa generale (2021)', 'GRI', '2', 'GRI che contiene le informative per le organizzazioni che devono fornire informazioni sulle loro prassi di rendicontazione, sulle attività e sui lavoratori, sulla governance, sulla strategia, sulle politiche e sulle  pratiche e sul coinvolgimento degli stakeholder', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Informativa generale (2021)', 'GRI - 2 (2021)', 'GRI 2-19 (b) (2021)', 'Norme riguardanti le remunerazioni');

