INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (477, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 11: La responsabilità di rispettare', 'UNGPs', 'Principio 11', 'Le imprese dovrebbero rispettare i diritti umani. Questo significa che esse dovrebbero evitare di violare i diritti umani di altre persone e affrontare gli impatti negativi sui diritti umani in cui esse siano coinvolte', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'La responsabilità di rispettare', 'UNGPs - Principio 11', 'UNGPs P.11', 'Le imprese dovrebbero rispettare i diritti umani. Questo significa che esse dovrebbero evitare di violare i diritti umani di altre persone e affrontare gli impatti negativi sui diritti umani in cui esse siano coinvolte');

-- Record 537/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (477, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 12: I diritti umani internazionalmente riconosciuti', 'UNGPs', 'Principio 12', 'La responsabilità delle imprese di rispettare i diritti umani si riferisce ai diritti umani internazionalmente riconosciuti – intesi, come minimo, quelli espressi nella Carta Internazionale dei Diritti Umani e i principi concernenti i diritti fondamentali enunciati nella Dichiarazione sui Principi e i Diritti Fondamentali nel Lavoro dell’Organizzazione Internazionale del Lavoro', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'I diritti umani internazionalmente riconosciuti', 'UNGPs - Principio 12', 'UNGPs P.12', 'La responsabilità delle imprese di rispettare i diritti umani si riferisce ai diritti umani internazionalmente riconosciuti – intesi, come minimo, quelli espressi nella Carta Internazionale dei Diritti Umani e i principi concernenti i diritti fondamentali enunciati nella Dichiarazione sui Principi e i Diritti Fondamentali nel Lavoro dell’Organizzazione Internazionale del Lavoro');

-- Record 538/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (477, 'Strategia Nazionale per lo Sviluppo Sostenibile (SNSvS)', 'Strategia Nazionale (IT)', 'SNSvS', 'In Italia rappresenta il quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali, declinando per il contesto nazionale gli Obiettivi di Sviluppo Sostenibile (SDG) delineati dall''ONU', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia Nazionale italiana per lo Sviluppo Sostenibile', 'SNSvS (2022)', 'SNSvS22- Prosperità IV.3', 'Promuovere la responsabilità sociale, ambientale e dei diritti umani nelle amministrazioni e nelle imprese, anche attraverso la finanza sostenibile');

-- Record 539/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (477, 'Direttiva 94/33/CE del Consiglio, del 22 giugno 1994, relativa alla protezione dei giovani sul lavoro', 'Direttiva (UE)', '94/33', 'Questa normativa estabilisce misure che riguardano le necessarie per vietare il lavoro dei bambini; che il lavoro degli adolescenti sia strettamente disciplinato e tutelato secondo le condizioni previste dalla presente direttiva e che ogni datore di lavoro garantisca ai giovani condizioni di lavoro appropriate alla loro età.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva relativa alla protezione dei giovani sul lavoro', 'Direttiva (UE) - 94/33', 'Direttiva (UE) 94/33', NULL);

-- Record 540/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (477, 'Direttiva (UE) 2022/2464 del Parlamento europeo e del Consiglio del 14 dicembre 2022 che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE)', '2022/2464', 'La CSRD o Corporate Sustainability Reporting Directive (Direttiva 2022/2464) è la direttiva europea che mira a migliorare la trasparenza e la qualità delle informazioni sulla sostenibilità fornite dalle aziende. La CSRD sostituisce e amplia la Non-Financial Reporting Directive (NFRD), aumentando le esigenze di rendicontazione per le imprese riguardo alle loro attività sostenibili. I punti chiavi della CSRD sono: -L''ampiezza del campo di applicazione rispetto alla NFRD rispetto alle aziende che devono rendicontare, - le informazioni da rendicontare in termini ESG e di sostenibilità; - lo standard di rendicontazione (gli ESRS); - L''audit e certificazione; e la digitalizzazione delle informazioni. 
 
 L''obiettivo principale della CSRD è migliorare la trasparenza e responsabilizzare le aziende verso pratiche più sostenibili, aiutando investitori, consumatori, e altre parti interessate a prendere decisioni informate basate su dati di sostenibilità accurati e completi.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE) - 2022/2464', 'CSRD', 'Rendicontazione societaria di sostenibilità (CSRD)');

-- Record 541/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (477, 'C182 - Convenzione sulle forme peggiori di lavoro minorile, 1999 ILO', 'ILO', 'Convenzione 182', 'La Convenzione 182 dell''Organizzazione Internazionale del Lavoro (ILO) sull''eliminazione delle peggiori forme di lavoro minorile, adottata nel 1999, è uno strumento internazionale chiave per la protezione dei diritti dei bambini e per l''eliminazione delle forme più dannose e pericolose di lavoro minorile.', 'L''Organizzazione Internazionale del Lavoro (OIL), fondata nel 1919, è un''agenzia specializzata delle Nazioni Unite che promuove i diritti nel lavoro, opportunità di occupazione dignitosa, protezione sociale e il dialogo sulle questioni lavorative. L’OIL definisce standard internazionali attraverso convenzioni e raccomandazioni, contribuendo a sostenere la giustizia sociale e i diritti umani nel mondo del lavoro. Un elemento distintivo è il suo approccio tripartito, che coinvolge governi, datori di lavoro e lavoratori per sviluppare politiche e programmi efficaci.

Gli standard internazionali del lavoro, elaborati dai costituenti dell''OIL, stabiliscono principi e diritti fondamentali nel lavoro. Questi strumenti si suddividono in: -Convenzioni e Protocolli, trattati internazionali legalmente vincolanti che possono essere ratificati dagli Stati membri; -Raccomandazioni, che offrono linee guida non vincolanti per orientare le politiche nazionali.
Per maggiore informazione vedere il sito: https://www.ilo.org/international-labour-standards', 'Sulle forme peggiori di lavoro minorile', 'Convenzione 182 - ILO', 'ILO- C182', 'Convenzione 182, ILO – mira alla protezione dei diritti dei bambini e l''eliminazione delle forme più dannose e pericolose di lavoro minorile');

-- Record 542/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (477, 'C29 - Convenzione sul Lavoro Forzato e Obbligatorio, 1930 - ILO', 'ILO', 'Convenzione 29', 'La Convenzione 29 dell''Organizzazione Internazionale del Lavoro (ILO) sul lavoro forzato, adottata nel 1930, è uno strumento internazionale che mira a sradicare il lavoro forzato o obbligatorio in tutte le sue forme. La Convenzione definisce il lavoro forzato come "qualsiasi lavoro o servizio imposto a una persona sotto la minaccia di una pena qualsiasi e per il quale detta persona non si è offerta di propria spontanea volontà".', 'L''Organizzazione Internazionale del Lavoro (OIL), fondata nel 1919, è un''agenzia specializzata delle Nazioni Unite che promuove i diritti nel lavoro, opportunità di occupazione dignitosa, protezione sociale e il dialogo sulle questioni lavorative. L’OIL definisce standard internazionali attraverso convenzioni e raccomandazioni, contribuendo a sostenere la giustizia sociale e i diritti umani nel mondo del lavoro. Un elemento distintivo è il suo approccio tripartito, che coinvolge governi, datori di lavoro e lavoratori per sviluppare politiche e programmi efficaci.

Gli standard internazionali del lavoro, elaborati dai costituenti dell''OIL, stabiliscono principi e diritti fondamentali nel lavoro. Questi strumenti si suddividono in: -Convenzioni e Protocolli, trattati internazionali legalmente vincolanti che possono essere ratificati dagli Stati membri; -Raccomandazioni, che offrono linee guida non vincolanti per orientare le politiche nazionali.
Per maggiore informazione vedere il sito: https://www.ilo.org/international-labour-standards', 'Sul Lavoro Forzato e Obbligatorio', 'Convenzione 29 - ILO', 'ILO- C29', 'Convenzione 29, ILO –  mira a sradicare il lavoro forzato o obbligatorio in tutte le sue forme');

-- Record 543/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (477, 'Decreto Legislativo 231/2001 - Modello di organizzazione e gestione', 'Decreto (IT)', '231/2001', 'Il Decreto Legislativo 231/2001, noto anche come "Modello di organizzazione, gestione e controllo" delle società, enti e associazioni, è una normativa italiana che introduce responsabilità amministrative e sanzioni per le persone giuridiche (società, enti, associazioni) in caso di reati commessi nell''interesse o a vantaggio di tali entità.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Modello di organizzazione e gestione', 'Decreto Legislativo - 231/2001', 'D.Lgs. 231/2001', NULL);

-- Record 544/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (477, 'Direttiva 2011/36/UE del Parlamento europeo e del Consiglio, del 5 aprile 2011, concernente la prevenzione e la repressione della tratta di esseri umani e la protezione delle vittime, e che sostituisce la decisione quadro del Consiglio 2002/629/GAI', 'Direttiva (UE)', '2011/36/UE', 'La direttiva 2011/36/UE stabilisce norme minime relative alla definizione dei reati e delle sanzioni nell’ambito della tratta di esseri umani. Essa introduce altresì disposizioni comuni, tenendo conto della prospettiva di genere, per rafforzare la prevenzione di tale reato e la protezione delle vittime.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Prevenzione e la repressione della tratta di esseri umani e la protezione delle vittime', 'Direttiva (UE) - 2011/36/UE', 'Direttiva (UE) 2011/36/UE', NULL);

-- Record 545/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (477, 'Direttiva (UE) 2024/1712 del Parlamento europeo e del Consiglio, del 13 giugno 2024, che modifica la direttiva 2011/36/UE concernente la prevenzione e la repressione della tratta di esseri umani e la protezione delle vittime', 'Direttiva (UE)', '2024/1712', 'La Direttiva (UE) 2024/1712, adottata il 13 giugno 2024, modifica la Direttiva 2011/36/UE sulla prevenzione e la repressione della tratta di esseri umani e la protezione delle vittime. Questa direttiva introduce nuove misure per migliorare la protezione delle vittime e dei minori coinvolti nella tratta di esseri umani. Tra le principali novità, c''è l''obbligo per gli Stati membri di non perseguire penalmente le vittime costrette a compiere attività illegali, l''istituzione di unità specializzate contro la tratta e la garanzia di accesso alla protezione internazionale e al risarcimento per le vittime. Inoltre, la direttiva prevede l''obbligo per gli Stati membri di adottare piani d''azione nazionali entro il 2028 e di istituire coordinatori nazionali anti-tratta per migliorare il coordinamento e l''efficacia delle azioni contro la tratta di esseri umani.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica la direttiva 2011/36/UE concernente la prevenzione e la repressione della tratta di esseri umani e la protezione delle vittime', 'Direttiva (UE) - 2024/1712', 'Direttiva (UE) 2024/1712', NULL);

-- Record 546/2644
INSERT INTO esg_impact.form_legal_bases
