INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (479, 'GRI 204- Prassi di approvvigionamento (2016)', 'GRI', '204', 'Questo Standard tratta il tema delle prassi di approvvigionamento. Illustra il supporto fornito da un’organizzazione a fornitori locali o a quelli di proprietà di donne o membri di gruppi vulnerabili. Tratta anche le modalità con cui le prassi di approvvigionamento dell’organizzazione (come i tempi di consegna che specifica ai fornitori o i prezzi di acquisto che contratta) causano impatti negativi sulla catena di fornitura o contribuiscono a tali impatti negativi.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Prassi di approvvigionamento (2016)', 'GRI - 204 (2016)', 'GRI 204-1 (a) (2016)', 'Proporzione della spesa effettuata a favore di fornitori locali');

