INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (479, 'ISO 20400: Guida agli approvvigionamenti sostenibili', 'ISO', '20400', 'ISO 20400 fornisce linee guida alle organizzazioni, indipendentemente dalla loro attività o dimensione, sull''integrazione della sostenibilità negli approvvigionamenti, come descritto nella ISO 26000. È destinata agli stakeholder coinvolti nelle decisioni e nei processi di approvvigionamento o che ne sono influenzati.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Guida agli approvvigionamenti sostenibili', 'ISO - 20400', 'ISO 20400', 'Guida agli approvvigionamenti sostenibili');

