INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (480, 'ESRS S3- Comunità', 'ESRS', 'S3', 'L''obiettivo di questo Standard è specificare i requisiti di informativa che consentiranno agli utenti della dichiarazione di sostenibilità di comprendere gli impatti rilevanti sulle comunità colpite, connessi con le operazioni proprie dell''impresa e con la catena del valore, anche attraverso i suoi prodotti o servizi, così come attraverso le sue relazioni commerciali, e i relativi rischi e opportunità materiali.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Comunità', 'ESRS - S3', 'ESRS (S3) S3-4 - AR 25 b; AR 37', 'Dichiarazione sugli investimenti sociali o altri programmi di sviluppo finalizzati a contribuire a un ulteriore impatto positivo materiale.');

-- Record 565/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (480, 'GRI 203- Impatti economici indiretti (2016)', 'GRI', '203', 'Lo Standard 203 tratta gli impatti economici indiretti, compresi quelli degli investimenti in infrastrutture e dei servizi supportatidall’organizzazione stessa.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Impatti economici indiretti (2016)', 'GRI - 203 (2016)', 'GRI 203-1 (2016)', 'Investimenti in infrastrutture e servizi supportati');

-- Record 566/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (480, 'WEF- Metriche del Capitalismo degli Stakeholder 2020 P4-3 (Comunità e vitalità sociale)', 'WEF', '4P: Metrica P4-3', 'Questa metrica è una definizione più inclusiva di investimento nella comunità, che cerca di catturare i molteplici modi in cui le aziende possono dimostrare il proprio investimento in attività sociali oltre i tradizionali investimenti di beneficenza.', 'Il World Economic Forum è l''Organizzazione Internazionale per la Cooperazione Pubblico-Privata. L''WEF insieme ad altre istituzioni ha sviluppato un insieme di metriche comuni per la rendicontazione della creazione di valore sostenibile (21 metriche principali e 34 metriche e divulgazioni ampliate) raggruprate in 4 pilastri principali (Principle of Governance, Planet, Persone and Prosperity). Per maggiore informazioni sulle 4 Pilastri (Metriche) di sostenibilità del WEF andare su: https://www.weforum.org/stakeholdercapitalism/our-metrics/', '3- Comunità e vitalità sociale', 'WEF (MSC2024) 4P- P4-3', 'WEF (MSC2024) 4P- P4-3 EM 1', 'Investimento sociale totale');

-- Record 567/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (480, 'Global Compact delle Nazioni Unite (UNGC): Principio 1- Diritti Umani', 'UNGC', 'Principio 1', 'Le imprese dovrebbero sostenere e rispettare la protezione dei diritti umani proclamati a livello internazionale', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Diritti Umani', 'UNGC - Principio 1', 'UNGC P.1', 'Le imprese dovrebbero sostenere e rispettare la protezione dei diritti umani proclamati a livello internazionale');

-- Record 568/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (480, 'Linee guida dell''OCSE per le imprese multinazionali sulla condotta responsabile delle imprese: Principio 2', 'OECD', 'Linee guida - Principio 2', 'Il principio 2 delle Linee Guida dell''OCSE stabilisce che le imprese dovrebbero: Rispettare i diritti umani internazionalmente riconosciuti delle persone interessate dalle loro attività.', 'Le Linee guida dell''OCSE per le imprese multinazionali sono raccomandazioni governative rivolte alle aziende, volte a promuovere una condotta responsabile. L''obiettivo è incentivare contributi positivi al progresso economico, ambientale e sociale, riducendo al contempo gli impatti negativi legati alle operazioni, ai prodotti e ai servizi aziendali. Le Linee guida coprono tutte le principali aree di responsabilità d''impresa, tra cui diritti umani, diritti dei lavoratori, ambiente, corruzione, interessi dei consumatori, trasparenza, scienza e tecnologia, concorrenza e fiscalità.', 'Linee guida per le imprese multinazionali sulla condotta responsabile delle imprese', 'OECD - Linee guida - Principio 2', 'Linee guida OECD P.2', 'Rispettare i diritti umani internazionalmente riconosciuti delle persone interessate dalle loro attività.');

-- Record 569/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (480, 'Linee guida dell''OCSE per le imprese multinazionali sulla condotta responsabile delle imprese: Principio 3', 'OECD', 'Linee guida - Principio 3', 'Il principio 3 delle Linee guida dell''OCSE per le imprese multinazionali stabilisce che le imprese dovrebbero: Incoraggiare lo sviluppo delle competenze locali tramite una stretta cooperazione con la comunità locale, inclusi gli ambienti degli affari, sviluppando nello stesso tempo le attività dell’impresa nei mercati interni ed esteri, compatibilmente con l’esigenza di una sana pratica commerciale.', 'Le Linee guida dell''OCSE per le imprese multinazionali sono raccomandazioni governative rivolte alle aziende, volte a promuovere una condotta responsabile. L''obiettivo è incentivare contributi positivi al progresso economico, ambientale e sociale, riducendo al contempo gli impatti negativi legati alle operazioni, ai prodotti e ai servizi aziendali. Le Linee guida coprono tutte le principali aree di responsabilità d''impresa, tra cui diritti umani, diritti dei lavoratori, ambiente, corruzione, interessi dei consumatori, trasparenza, scienza e tecnologia, concorrenza e fiscalità.', 'Linee guida per le imprese multinazionali sulla condotta responsabile delle imprese', 'OECD - Linee guida - Principio 3', 'Linee guida OECD P.3', 'Incoraggiare lo sviluppo delle competenze locali tramite una stretta cooperazione con la comunità locale, inclusi gli ambienti degli affari, sviluppando nello stesso tempo le attività dell’impresa nei mercati interni ed esteri, compatibilmente con l’esigenza di una sana pratica commerciale.');

