INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (481, 'Global Compact delle Nazioni Unite (UNGC): Principio 9- Ambiente', 'UNGC', 'Principio 9', 'Le imprese dovrebbero incoraggiare lo sviluppo e la diffusione di tecnologie rispettose dell''ambiente', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Ambiente', 'UNGC - Principio 9', 'UNGC P.9', 'Le imprese dovrebbero incoraggiare lo sviluppo e la diffusione di tecnologie rispettose dell''ambiente');

