INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (484, 'ISO 30415: Gestione delle risorse umane - Diversità e inclusione', 'ISO', '30415', 'L''ISO 30415 è lo standard che fornisce linee guida sulla diversità e inclusione per le organizzazioni, compreso il loro organo di governo, i leader, la forza lavoro e i rappresentanti riconosciuti, e altri portatori di interessi.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Gestione delle risorse umane - Diversità e inclusione', 'ISO - 30415', 'ISO 30415', 'Gestione delle risorse umane - Diversità e inclusione');

-- Record 621/2644
