INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (486, 'Strategia per la parità di genere (UE)', 'Strategia (UE)', 'Parità di genere', 'La strategia dell''UE per la parità di genere presenta gli obiettivi strategici e le azioni volte a compiere progressi significativi entro il 2025 verso un''Europa garante della parità di genere. 
 
 Gli obiettivi principali sono porre fine alla violenza di genere; combattere gli stereotipi di genere; colmare il divario di genere nel mercato del lavoro; raggiungere la parità nella partecipazione ai diversi settori economici; far fronte al problema del divario retributivo e pensionistico fra uomini e donne; colmare il divario e conseguire l''equilibrio di genere nel processo decisionale e nella politica.', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Strategia dell''UE per la parità di genere', 'Strategia (UE) - Parità di genere', 'Strategia (UE) Parità di genere', 'Strategia per la parità di genere (UE)');

-- Record 646/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (486, 'Linee guida dell''OCSE per le imprese multinazionali sulla condotta responsabile delle imprese: Principio 2', 'OECD', 'Linee guida - Principio 2', 'Il principio 2 delle Linee Guida dell''OCSE stabilisce che le imprese dovrebbero: Rispettare i diritti umani internazionalmente riconosciuti delle persone interessate dalle loro attività.', 'Le Linee guida dell''OCSE per le imprese multinazionali sono raccomandazioni governative rivolte alle aziende, volte a promuovere una condotta responsabile. L''obiettivo è incentivare contributi positivi al progresso economico, ambientale e sociale, riducendo al contempo gli impatti negativi legati alle operazioni, ai prodotti e ai servizi aziendali. Le Linee guida coprono tutte le principali aree di responsabilità d''impresa, tra cui diritti umani, diritti dei lavoratori, ambiente, corruzione, interessi dei consumatori, trasparenza, scienza e tecnologia, concorrenza e fiscalità.', 'Linee guida per le imprese multinazionali sulla condotta responsabile delle imprese', 'OECD - Linee guida - Principio 2', 'Linee guida OECD P.2', 'Rispettare i diritti umani internazionalmente riconosciuti delle persone interessate dalle loro attività.');

-- Record 647/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (486, 'Strategia Nazionale per la parità di genere 2021-2026: Target Reddito', 'Strategia Nazionale (IT)', 'Parità di genere - Target: Reddito', 'Con l’obiettivo di diminuire i differenziali retributivi di genere, in particolare nel settore privato e per le posizioni che richiedono un livello medio-alto di competenze tecniche e manageriali, nella Strategia Nazionale per la parità di genere, vengono misurati: - Il gender pay gap nel settore privato e - il gender pay gap per i lavoratori laureati.', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia Nazionale per la parità di genere 2021-2026', 'Strategia Nazionale - Parità di genere - Target: Reddito', 'Strategia (IT) Parità di genere- Target: Reddito', 'Strategia Nazionale per la parità di genere: Target Reddito');

-- Record 648/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (486, 'Legge 162/2021 - Modifiche al codice di cui al Decreto Legislativo 11 aprile 2006, n. 198, e altre disposizioni in materia di pari opportunita'' tra uomo e donna in ambito lavorativo', 'Legge (IT)', '162/2021', 'La legge 162/2021 ha introdotto nuove regole per promuovere la parità tra uomini e donne sul lavoro e introduce anche la certificazione di genere, che le aziende possono ottenere per attestare pari retribuzione e carriera tra generi, ottenendo esenzioni contributive fino all''1% sui salari e fino a 50.000 euro annui.', 'Il termine legge viene usato nel diritto con una molteplicità di significati, si distingue in:
la norma giuridica; l’atto normativo (legge in senso materiale); l’atto giuridico attraverso il quale il potere legislativo esercita la sua funzione (legge in senso formale); l’insieme delle norme giuridiche che costituiscono l’ordinamento giuridico, ossia il diritto oggettivo. Per maggiori informazioni accedere al link:
https://www.governo.it/it/costituzione-italiana/principi-fondamentali/2839', 'Modifiche al codice di cui al Decreto Legislativo 11 aprile 2006, n. 198, e altre disposizioni in materia di pari opportunita'' tra uomo e donna in ambito lavorativo', 'Legge (IT) - 162/2021', 'Legge 162/2021', NULL);

