INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (486, 'ISO 30415: Gestione delle risorse umane - Diversità e inclusione', 'ISO', '30415', 'L''ISO 30415 è lo standard che fornisce linee guida sulla diversità e inclusione per le organizzazioni, compreso il loro organo di governo, i leader, la forza lavoro e i rappresentanti riconosciuti, e altri portatori di interessi.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Gestione delle risorse umane - Diversità e inclusione', 'ISO - 30415', 'ISO 30415', 'Gestione delle risorse umane - Diversità e inclusione');

-- Record 653/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (486, 'Costituzione Italiana: Articolo 37: Parità tra uomo e donna in materia di lavoro', 'Costituzione Italiana', 'Articolo 37', 'La donna lavoratrice ha gli stessi diritti e, a parità di lavoro, le stesse retribuzioni che spettano al lavoratore. Le condizioni di lavoro devono consentire l’adempimento della sua essenziale funzione familiare e assicurare alla madre e al bambino una speciale adeguata protezione.', 'La Costituzione Italiana è la legge fondamentale della Repubblica Italiana. Essa stabilisce i principi fondamentali su cui si basa l''ordinamento dello Stato italiano, definendo i diritti e i doveri dei cittadini, l''organizzazione e i poteri delle istituzioni statali (Parlamento, Governo e Magistratura), nonché i principi economici e sociali dello Stato. La Costituzione è la norma suprema, alla quale devono conformarsi tutte le altre leggi e normative del Paese.', 'Parità tra uomo e donna in materia di lavoro', 'Costituzione Italiana - Articolo 37', 'Art. 37 Cost.', 'Parità tra uomo e donna in materia di lavoro');

-- Record 654/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (486, 'Dichiarazione dell’OIL sui principi e diritti fondamentali del lavoro e i suoi seguiti', 'ILO', 'Dichiarazione (PDFL)', 'La Dichiarazione dell’OIL afferma che tutti i membri, anche senza aver ratificato le convenzioni pertinenti, sono obbligati a rispettare, promuovere e attuare i principi sui diritti fondamentali del lavoro. Questi includono: libertà di associazione e diritto alla contrattazione collettiva, abolizione del lavoro forzato, eliminazione del lavoro minorile, non discriminazione in ambito lavorativo e creazione di un ambiente di lavoro sicuro e salubre.', 'L''Organizzazione Internazionale del Lavoro (OIL), fondata nel 1919, è un''agenzia specializzata delle Nazioni Unite che promuove i diritti nel lavoro, opportunità di occupazione dignitosa, protezione sociale e il dialogo sulle questioni lavorative. L’OIL definisce standard internazionali attraverso convenzioni e raccomandazioni, contribuendo a sostenere la giustizia sociale e i diritti umani nel mondo del lavoro. Un elemento distintivo è il suo approccio tripartito, che coinvolge governi, datori di lavoro e lavoratori per sviluppare politiche e programmi efficaci.

Gli standard internazionali del lavoro, elaborati dai costituenti dell''OIL, stabiliscono principi e diritti fondamentali nel lavoro. Questi strumenti si suddividono in: -Convenzioni e Protocolli, trattati internazionali legalmente vincolanti che possono essere ratificati dagli Stati membri; -Raccomandazioni, che offrono linee guida non vincolanti per orientare le politiche nazionali.
Per maggiore informazione vedere il sito: https://www.ilo.org/international-labour-standards', 'Dichiarazione sui principi e diritti fondamentali del lavoro', 'ILO- Dichiarazione (PDFL)', 'PDFL- ILO', NULL);

-- Record 655/2644
INSERT INTO esg_impact.form_legal_bases
