INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (490, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 18: L’identificazione e valutazione dell’impatto negativo sui diritti umani', 'UNGPs', 'Principio 18', 'Il Principio 18 sottolinea l''importanza per le imprese di identificare e valutare gli impatti negativi sui diritti umani che possono derivare dalle loro attività e relazioni commerciali. Questo vale sia per le azioni dirette dell''azienda stessa, sia per quelle delle aziende con cui collabora (fornitori, clienti, ecc.).', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'L’identificazione e valutazione dell’impatto negativo sui diritti umani', 'UNGPs - Principio 18', 'UNGPs P.18', 'Il Principio 18 sottolinea l''importanza per le imprese di identificare e valutare gli impatti negativi sui diritti umani che possono derivare dalle loro attività e relazioni commerciali. Questo vale sia per le azioni dirette dell''azienda stessa, sia per quelle delle aziende con cui collabora (fornitori, clienti, ecc.).');

