INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (491, 'SDGs: Goal 12 Consumo e produzione responsabili', 'SDGs', 'Goal 12', 'Garantire modelli sostenibili di produzione e di consumo', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Consumo e produzione responsabili', 'SDGs - Goal 12', 'SDG 12.6', 'Incoraggiare le imprese, in particolare le grandi aziende multinazionali, ad adottare pratiche sostenibili e ad integrare le informazioni sulla sostenibilità nei loro resoconti annuali');

-- Record 705/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (491, 'GRI 414- Valutazione sociale dei fornitori (2016)', 'GRI', '414', 'Questo Standard contiene informative per le organizzazioni che devono rendicontare sui loro impatti sociali nella loro catena di fornitura, e come li gestiscono. Le informative consentono a un’organizzazione di fornire informazioni sul suo approccio alla prevenzione e mitigazione di impatti sociali negativi nella sua catena di fornitura.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Valutazione sociale dei fornitori (2016)', 'GRI - 414 (2016)', 'GRI 414-2 (c) (2016)', 'L’organizzazione deve rendicontare le seguenti informazioni: c. Significativi impatti sociali negativi, potenziali ed effettivi, identificati nella catena di fornitura.');

-- Record 706/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (491, 'ESRS G1- Condotta aziendale', 'ESRS', 'G1', 'L''obiettivo di questo Standard è specificare i requisiti di divulgazione che consentiranno agli utenti delle dichiarazioni di sostenibilità dell''azienda di comprendere la strategia e l''approccio dell''azienda, i processi e le procedure, nonché le sue performance riguardo alla condotta aziendale.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Condotta aziendale', 'ESRS - G1', 'ESRS (G1) G1-2 15 b; AR2- AR 3', 'Divulgazione di come i criteri sociali e ambientali vengono presi in considerazione per la selezione dei partner contrattuali dal lato dell''offerta.');

-- Record 707/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (491, 'Global Compact delle Nazioni Unite (UNGC): Principio 7- Ambiente', 'UNGC', 'Principio 7', 'Le imprese dovrebbero sostenere un approccio precauzionale alle sfide ambientali', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Ambiente', 'UNGC - Principio 7', 'UNGC P.7', 'Le imprese dovrebbero sostenere un approccio precauzionale alle sfide ambientali');

-- Record 708/2644
