INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (491, 'Linee guida dell''OCSE per le imprese multinazionali sulla condotta responsabile delle imprese: Principio 10', 'OECD', 'Linee guida - Principio 10', 'Il principio 10 stabilisce che le imprese dovrebbero: Le imprese doverebbero astenersi e adottare misure per prevenire l''uso di ritorsioni, anche da parte di entità con cui l''impresa ha rapporti commerciali, contro persone o gruppi che potrebbero cercare o effettivamente investigare o sollevare preoccupazioni riguardanti impatti negativi effettivi o potenziali associati alle operazioni, prodotti o servizi dell''impresa. Questo include promuovere un ambiente in cui individui e gruppi si sentano al sicuro nel sollevare preoccupazioni e, ove opportuno, contribuire alla mitigazione degli impatti negativi delle ritorsioni quando si verificano.', 'Le Linee guida dell''OCSE per le imprese multinazionali sono raccomandazioni governative rivolte alle aziende, volte a promuovere una condotta responsabile. L''obiettivo è incentivare contributi positivi al progresso economico, ambientale e sociale, riducendo al contempo gli impatti negativi legati alle operazioni, ai prodotti e ai servizi aziendali. Le Linee guida coprono tutte le principali aree di responsabilità d''impresa, tra cui diritti umani, diritti dei lavoratori, ambiente, corruzione, interessi dei consumatori, trasparenza, scienza e tecnologia, concorrenza e fiscalità.', 'Linee guida per le imprese multinazionali sulla condotta responsabile delle imprese', 'OECD - Linee guida - Principio 10', 'Linee guida OECD P.10', 'Le imprese doverebbero astenersi e adottare misure per prevenire l''uso di ritorsioni, anche da parte di entità con cui l''impresa ha rapporti commerciali, contro persone o gruppi che potrebbero cercare o effettivamente investigare o sollevare preoccupazioni riguardanti impatti negativi effettivi o potenziali associati alle operazioni, prodotti o servizi dell''impresa [...]');

