INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (491, 'Regolamento (UE) 2019/2088 del Parlamento europeo e del Consiglio del 27 novembre 2019 relativo all’informativa sulla sostenibilità nel settore dei servizi finanziari', 'Regolamento (UE)', '2019/2088', 'Questo regolamento stabilisce norme armonizzate sulla trasparenza per i partecipanti ai mercati finanziari e i consulenti finanziari per quanto riguarda l’integrazione dei rischi di sostenibilità e la considerazione degli effetti negativi per la sostenibilità nei loro processi e nella comunicazione delle informazioni connesse alla sostenibilità relative ai prodotti finanziari.', 'I regolamenti sono atti giuridici definiti nell’articolo 288 del trattato sul funzionamento dell’Unione europea (TFUE). Hanno portata generale, sono vincolanti in tutti i loro elementi e direttamente applicabili negli Stati membri dell’Unione. I regolamenti rientrano nel diritto derivato dell’Unione. Sono adottati dalle istituzioni dell’Unione europea sulla base dei trattati. Un regolamento ha carattere vincolante in tutti i suoi elementi, ha portata generale ed è rivolto a categorie astratte di persone, non a specifici individui. 
 https://eur-lex.europa.eu/IT/legal-content/summary/european-union-regulations.html', 'Regolamento relativo all’informativa sulla sostenibilità nel settore dei servizi finanziari', 'Regolamento (UE) - 2019/2088', 'Regolamento (UE) 2019/2088', NULL);

-- Record 715/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (491, 'Direttiva (UE) 2022/2464 del Parlamento europeo e del Consiglio del 14 dicembre 2022 che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE)', '2022/2464', 'La CSRD o Corporate Sustainability Reporting Directive (Direttiva 2022/2464) è la direttiva europea che mira a migliorare la trasparenza e la qualità delle informazioni sulla sostenibilità fornite dalle aziende. La CSRD sostituisce e amplia la Non-Financial Reporting Directive (NFRD), aumentando le esigenze di rendicontazione per le imprese riguardo alle loro attività sostenibili. I punti chiavi della CSRD sono: -L''ampiezza del campo di applicazione rispetto alla NFRD rispetto alle aziende che devono rendicontare, - le informazioni da rendicontare in termini ESG e di sostenibilità; - lo standard di rendicontazione (gli ESRS); - L''audit e certificazione; e la digitalizzazione delle informazioni. 
 
 L''obiettivo principale della CSRD è migliorare la trasparenza e responsabilizzare le aziende verso pratiche più sostenibili, aiutando investitori, consumatori, e altre parti interessate a prendere decisioni informate basate su dati di sostenibilità accurati e completi.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE) - 2022/2464', 'CSRD', 'Rendicontazione societaria di sostenibilità (CSRD)');

-- Record 716/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (491, 'Direttiva (UE) 2024/1760 del Parlamento europeo e del Consiglio, del 13 giugno 2024, relativa al dovere di diligenza delle imprese ai fini della sostenibilità e che modifica la direttiva (UE) 2019/1937 e il regolamento (UE) 2023/2859', 'Direttiva (UE)', '2024/1760', 'L''obiettivo della CSDDD è promuovere un comportamento societario sostenibile e responsabile nelle attività delle società e nelle loro catene globali del valore.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva relativa al dovere di diligenza delle imprese ai fini della sostenibilità', 'Direttiva (UE) - 2024/1760', 'CSDDD', 'Direttiva relativa al dovere di diligenza delle imprese ai fini della sostenibilità');

