INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (491, 'Guida dell’OCSE sul dovere di diligenza per la condotta d’impresa responsabile', 'OECD', 'Due diligence', 'Le linee guida dell''Organizzazione per la Cooperazione e lo Sviluppo Economico (OCSE) per la due diligence responsabile forniscono un quadro per aiutare le imprese a identificare, prevenire, mitigare e affrontare gli impatti negativi delle loro attività sulle persone, sull''ambiente e sulla società. Queste linee guida sono parte integrante delle Linee Guida dell''OCSE destinate alle imprese multinazionali e sono applicabili a tutte le imprese, indipendentemente dalle dimensioni o dal settore.', 'Le Linee guida dell''OCSE per le imprese multinazionali sono raccomandazioni governative rivolte alle aziende, volte a promuovere una condotta responsabile. L''obiettivo è incentivare contributi positivi al progresso economico, ambientale e sociale, riducendo al contempo gli impatti negativi legati alle operazioni, ai prodotti e ai servizi aziendali. Le Linee guida coprono tutte le principali aree di responsabilità d''impresa, tra cui diritti umani, diritti dei lavoratori, ambiente, corruzione, interessi dei consumatori, trasparenza, scienza e tecnologia, concorrenza e fiscalità.', 'Guida sul dovere di diligenza per la condotta d’impresa responsabile', 'OECD - Due diligence', 'OECD - Due diligence', NULL);

-- Record 718/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (491, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 17: La due diligence in materia di diritti umani', 'UNGPs', 'Principio 17', 'Il principio 17  stabilisce che: "Per poter identificare, prevenire e mitigare i propri impatti negativi sui diritti umani e per rendere conto del modo in cui siffatti impatti vengono affrontati, le imprese dovrebbero eseguire la due diligence sui diritti umani. Tale processo dovrebbe includere la valutazione dell’impatto effettivo e di quello potenziale sui diritti umani, l’integrazione dei risultati di tale valutazione, le misure da adottare in conseguenza, il monitoraggio delle risposte, e la comunicazione circa le modalità con cui siffatti impatti sono stati affrontati".', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'La due diligence in materia di diritti umani', 'UNGPs - Principio 17', 'UNGPs P.17', 'Il principio 17 stabilisce che: "Per poter identificare, prevenire e mitigare i propri impatti negativi sui diritti umani e per rendere conto del modo in cui siffatti impatti vengono affrontati, le imprese dovrebbero eseguire la due diligence sui diritti umani. Tale processo dovrebbe includere la valutazione dell’impatto effettivo e di quello potenziale sui diritti umani, l’integrazione dei risultati di tale valutazione, le misure da adottare in conseguenza, il monitoraggio delle risposte, e la comunicazione circa le modalità con cui siffatti impatti sono stati affrontati".');

-- Record 719/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (491, 'Principi Guida delle Nazioni Unite su Imprese e Diritti Umani (UNGPs): Principio 18: L’identificazione e valutazione dell’impatto negativo sui diritti umani', 'UNGPs', 'Principio 18', 'Il Principio 18 sottolinea l''importanza per le imprese di identificare e valutare gli impatti negativi sui diritti umani che possono derivare dalle loro attività e relazioni commerciali. Questo vale sia per le azioni dirette dell''azienda stessa, sia per quelle delle aziende con cui collabora (fornitori, clienti, ecc.).', 'I Principi Guida delle Nazioni Unite su Imprese e Diritti Umani sono un insieme di linee guida per Stati e aziende al fine di prevenire e affrontare le violazioni dei diritti umani commesse nelle operazioni commerciali. I Principi Guida contengono tre capitoli, o pilastri: proteggere, rispettare e rimediare. Ognuno definisce passaggi concreti e attuabili affinché i governi e le aziende possano adempiere ai rispettivi doveri e responsabilità per prevenire le violazioni dei diritti umani nelle operazioni aziendali e fornire rimedi in caso di tali violazioni. Per maggiore informazioni consultare: https://www.ohchr.org/documents/publications/guidingprinciplesbusinesshr_en.pdf', 'L’identificazione e valutazione dell’impatto negativo sui diritti umani', 'UNGPs - Principio 18', 'UNGPs P.18', 'Il Principio 18 sottolinea l''importanza per le imprese di identificare e valutare gli impatti negativi sui diritti umani che possono derivare dalle loro attività e relazioni commerciali. Questo vale sia per le azioni dirette dell''azienda stessa, sia per quelle delle aziende con cui collabora (fornitori, clienti, ecc.).');

