INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (491, 'Decreto Legislativo 231/2001 - Modello di organizzazione e gestione', 'Decreto (IT)', '231/2001', 'Il Decreto Legislativo 231/2001, noto anche come "Modello di organizzazione, gestione e controllo" delle società, enti e associazioni, è una normativa italiana che introduce responsabilità amministrative e sanzioni per le persone giuridiche (società, enti, associazioni) in caso di reati commessi nell''interesse o a vantaggio di tali entità.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Modello di organizzazione e gestione', 'Decreto Legislativo - 231/2001', 'D.Lgs. 231/2001', NULL);

-- Record 724/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (491, 'Direttiva (UE) 2024/825 del Parlamento europeo e del Consiglio, del 28 febbraio 2024, che modifica le direttive 2005/29/CE e 2011/83/UE per quanto riguarda la responsabilizzazione dei consumatori per la transizione verde mediante il miglioramento della tutela dalle pratiche sleali e dell’informazione', 'Direttiva (UE)', '2024/825', 'La Direttiva (UE) 2024/825, si propone di contrastare il greenwashing. Questa direttiva mira a promuovere una maggiore trasparenza fiscale delle grandi multinazionali operanti nell''UE, facilitare la cooperazione tra le autorità fiscali degli Stati membri e migliorare l''efficacia delle normative fiscali europee per prevenire l''evasione fiscale e promuovere una concorrenza equa e trasparente nel mercato interno.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica le direttive 2005/29/CE e 2011/83/UE per quanto riguarda la responsabilizzazione dei consumatori per la transizione verde mediante il miglioramento della tutela dalle pratiche sleali e dell’informazione (Direttiva contro il Greenwashing)', 'Direttiva (UE) - 2024/825', 'Direttiva (UE) 2024/825', NULL);

-- Record 725/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (491, 'Linee guida per contribuire agli Obiettivi di sviluppo sostenibile (SDG) delle Nazioni Unite (ISO/UNDP PAS 53002)', 'ISO', 'UNDP PAS 53002:2024', 'Lo scopo delle "Linee guida sullo sviluppo sostenibile" è quello di aiutare le organizzazioni a gestire e ottimizzare il loro contributo al raggiungimento degli Obiettivi di Sviluppo Sostenibile (SDG) delle Nazioni Unite.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Linee guida per contribuire agli Obiettivi di sviluppo sostenibile', 'ISO - UNDP PAS 53002:2024', 'ISO UNDP PAS 53002:2024', 'Linee guida per contribuire agli Obiettivi di sviluppo sostenibile');

-- Record 726/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (492, 'SDGs: Goal 12 Consumo e produzione responsabili', 'SDGs', 'Goal 12', 'Garantire modelli sostenibili di produzione e di consumo', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Consumo e produzione responsabili', 'SDGs - Goal 12', 'SDG 12.6', 'Incoraggiare le imprese, in particolare le grandi aziende multinazionali, ad adottare pratiche sostenibili e ad integrare le informazioni sulla sostenibilità nei loro resoconti annuali');

-- Record 727/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (492, 'SDGs: Goal 8 Lavoro dignitoso e crescita economica', 'SDGs', 'Goal 8', 'Incentivare una crescita economica duratura, inclusiva e sostenibile, un’occupazione piena e produttiva ed un lavoro dignitoso per tutti', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Lavoro dignitoso e crescita economica', 'SDGs - Goal 8', 'SDG 8.4', 'Migliorare progressivamente, entro il 2030, l’efficienza globale nel consumo e nella produzione di risorse e tentare di scollegare la crescita economica dalla degradazione ambientale, conformemente al Quadro decennale di programmi relativi alla produzione e al consumo sostenibile, con i paesi più sviluppati in prima linea');

