INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (493, 'Direttiva (UE) 2019/904 del Parlamento europeo e del Consiglio, del 5 giugno 2019, sulla riduzione dell’incidenza di determinati prodotti di plastica sull’ambiente', 'Direttiva (UE)', '2019/904', 'Gli obiettivi di questa direttiva sono prevenire e ridurre l’incidenza di determinati prodotti di plastica sull’ambiente, in particolare l’ambiente acquatico, e sulla salute umana, nonché promuovere la transizione verso un’economia circolare con modelli imprenditoriali, prodotti e materiali innovativi e sostenibili, contribuendo in tal modo al corretto funzionamento del mercato interno.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva sulla riduzione dell’incidenza di determinati prodotti di plastica sull’ambiente', 'Direttiva (UE) - 2019/904', 'Direttiva (UE) 2019/904', NULL);

