INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (494, 'Principi di Corporate Governance G20/OECD (2023)- Principio IV', 'Principi G20/OECD', 'Principio IV', 'Il principio IV stabilische che il quadro di governance aziendale dovrebbe garantire una divulgazione tempestiva e accurata di tutte le questioni rilevanti riguardanti l''azienda, inclusa la situazione finanziaria, le prestazioni, la sostenibilità, la proprietà e la governance della società.', 'I Principi di Corporate Governance G20/OCSE sono lo standard internazionale per il governo societario. I Principi aiutano i responsabili politici a valutare e migliorare il quadro legale, regolamentare e istituzionale per il governo societario, con l''obiettivo di supportare l''efficienza economica, la crescita sostenibile e la stabilità finanziaria. Per maggiori informazioni vedere: https://legalinstruments.oecd.org/public/doc/322/322.en.pdf', 'Divulgazione e trasparenza', 'Principi - Corporate Governance G20/OECD - IV', 'Principi G20/OECD: IV.A.2', 'Obiettivi aziendali e informazioni relative alla sostenibilità.');

