INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (500, 'Decreto Legislativo 231/2001 - Modello di organizzazione e gestione', 'Decreto (IT)', '231/2001', 'Il Decreto Legislativo 231/2001, noto anche come "Modello di organizzazione, gestione e controllo" delle società, enti e associazioni, è una normativa italiana che introduce responsabilità amministrative e sanzioni per le persone giuridiche (società, enti, associazioni) in caso di reati commessi nell''interesse o a vantaggio di tali entità.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Modello di organizzazione e gestione', 'Decreto Legislativo - 231/2001', 'D.Lgs. 231/2001', NULL);

-- Record 832/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (500, 'ISO 31000: Gestione del Rischio', 'ISO', '31000', 'ISO 31000 è una norma internazionale sviluppata dall''Organizzazione Internazionale per la Normazione (ISO) che fornisce linee guida per la gestione del rischio. ISO 31000 fornisce un quadro solido e strutturato per la gestione del rischio, promuovendo una cultura organizzativa orientata alla prevenzione e alla mitigazione dei rischi per garantire la continuità operativa e il raggiungimento degli obiettivi strategici.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Gestione del Rischio', 'ISO - 31000', 'ISO 31000', NULL);

