INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (501, 'Global Compact delle Nazioni Unite (UNGC): Principio 1- Diritti Umani', 'UNGC', 'Principio 1', 'Le imprese dovrebbero sostenere e rispettare la protezione dei diritti umani proclamati a livello internazionale', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Diritti Umani', 'UNGC - Principio 1', 'UNGC P.1', 'Le imprese dovrebbero sostenere e rispettare la protezione dei diritti umani proclamati a livello internazionale');

-- Record 837/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (501, 'Linee guida dell''OCSE per le imprese multinazionali sulla condotta responsabile delle imprese: Principio 2', 'OECD', 'Linee guida - Principio 2', 'Il principio 2 delle Linee Guida dell''OCSE stabilisce che le imprese dovrebbero: Rispettare i diritti umani internazionalmente riconosciuti delle persone interessate dalle loro attività.', 'Le Linee guida dell''OCSE per le imprese multinazionali sono raccomandazioni governative rivolte alle aziende, volte a promuovere una condotta responsabile. L''obiettivo è incentivare contributi positivi al progresso economico, ambientale e sociale, riducendo al contempo gli impatti negativi legati alle operazioni, ai prodotti e ai servizi aziendali. Le Linee guida coprono tutte le principali aree di responsabilità d''impresa, tra cui diritti umani, diritti dei lavoratori, ambiente, corruzione, interessi dei consumatori, trasparenza, scienza e tecnologia, concorrenza e fiscalità.', 'Linee guida per le imprese multinazionali sulla condotta responsabile delle imprese', 'OECD - Linee guida - Principio 2', 'Linee guida OECD P.2', 'Rispettare i diritti umani internazionalmente riconosciuti delle persone interessate dalle loro attività.');

-- Record 838/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (501, 'Strategia Nazionale per lo Sviluppo Sostenibile (SNSvS)', 'Strategia Nazionale (IT)', 'SNSvS', 'In Italia rappresenta il quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali, declinando per il contesto nazionale gli Obiettivi di Sviluppo Sostenibile (SDG) delineati dall''ONU', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia Nazionale italiana per lo Sviluppo Sostenibile', 'SNSvS (2022)', 'SNSvS22- Prosperità IV.3', 'Promuovere la responsabilità sociale, ambientale e dei diritti umani nelle amministrazioni e nelle imprese, anche attraverso la finanza sostenibile');

-- Record 839/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (501, 'Direttiva (UE) 2022/2464 del Parlamento europeo e del Consiglio del 14 dicembre 2022 che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE)', '2022/2464', 'La CSRD o Corporate Sustainability Reporting Directive (Direttiva 2022/2464) è la direttiva europea che mira a migliorare la trasparenza e la qualità delle informazioni sulla sostenibilità fornite dalle aziende. La CSRD sostituisce e amplia la Non-Financial Reporting Directive (NFRD), aumentando le esigenze di rendicontazione per le imprese riguardo alle loro attività sostenibili. I punti chiavi della CSRD sono: -L''ampiezza del campo di applicazione rispetto alla NFRD rispetto alle aziende che devono rendicontare, - le informazioni da rendicontare in termini ESG e di sostenibilità; - lo standard di rendicontazione (gli ESRS); - L''audit e certificazione; e la digitalizzazione delle informazioni. 
 
 L''obiettivo principale della CSRD è migliorare la trasparenza e responsabilizzare le aziende verso pratiche più sostenibili, aiutando investitori, consumatori, e altre parti interessate a prendere decisioni informate basate su dati di sostenibilità accurati e completi.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE) - 2022/2464', 'CSRD', 'Rendicontazione societaria di sostenibilità (CSRD)');

-- Record 840/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (501, 'Decreto Legislativo 6 settembre 2011, n. 159 (Codice delle leggi antimafia e delle misure di prevenzione, nonche'' nuove disposizioni in materia di documentazione antimafia, a norma degli articoli 1 e 2 della legge 13 agosto 2010, n. 136)', 'Decreto (IT)', '159', 'Il D. Lgs. 159 stabilisce il Codice delle leggi antimafia e delle misure di prevenzione. Il decreto mira a migliorare l''efficacia delle indagini e delle azioni preventive contro la criminalità organizzata, contribuendo alla sicurezza e al rispetto delle norme nel contesto italiano.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Codice delle leggi antimafia e delle misure di prevenzione, nonche'' nuove disposizioni in materia di documentazione antimafia', 'Decreto Legislativo - 159', 'D.Lgs. 159/2011', NULL);

-- Record 841/2644
INSERT INTO esg_impact.form_legal_bases
