INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (507, 'Protocollo di Kyoto della Convenzione Quadro delle Nazioni Unite sui Cambiamenti Climatici', 'Protocollo (Int.)', 'Cambiamenti Climatici (Protocollo Kyoto)', 'Il protocollo di Kyoto, che fa seguito alla convenzione quadro delle Nazioni Unite sui cambiamenti climatici, è uno dei più importanti strumenti giuridici internazionali volti a combattere i cambiamenti climatici. Esso contiene gli impegni dei paesi industrializzati a ridurre le emissioni di alcuni gas ad effetto serra, responsabili del riscaldamento del pianeta.', 'Un protocollo è un tipo di accordo formale tra Stati o altre entità sovrane che integra, modifica o specifica ulteriormente un trattato esistente. I protocolli sono strumenti giuridici che possono avere diverse funzioni e scopi, tra cui l''espansione di disposizioni di un trattato, la creazione di nuovi obblighi o la regolamentazione di questioni specifiche non completamente coperte dal trattato originale.', 'Quadro delle Nazioni Unite sui Cambiamenti Climatici', 'Protocollo - Cambiamenti Climatici (Protocollo Kyoto)', 'Protocollo Kyoto', 'Convenzione Quadro delle Nazioni Unite sui Cambiamenti Climatici');

-- Record 902/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (507, 'Accordo di Parigi sui cambiamenti climatici', 'Trattato internazionale', 'Accordo di Parigi sui Cambiamenti climatici', 'L''accordo di Parigi è un trattato internazionale che presenta un piano d''azione per limitare il riscaldamento globale. L’accordo di Parigi, firmato da 194 paesi e dall’UE, mira a limitare il riscaldamento globale al di sotto di 2°C e a proseguire gli sforzi per circoscriverlo a 1,5°C al fine di evitare le conseguenze catastrofiche del cambiamento climatico.', 'L''accordo internazionale può essere considerato come un contratto stipulato tra due o più Stati, diretto a regolare una determinata sfera di rapporti tra i contraenti. La sua formazione è regolata da una serie di norme consuetudinarie e convenzionali che rientrano nel diritto pubblico internazionale.', 'Accordo sui cambiamenti climatici', 'Trattato internazionale - Accordo di Parigi sui Cambiamenti climatici', 'Accordo Parigi (2015)', 'Accordo che mira a limitare il riscaldamento globale al di sotto di 2°C');

-- Record 903/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (507, 'Protocollo di Montreal relativo a sostanze che riducono lo strato di ozono', 'Protocollo (Int.)', 'Sostanze che riducono lo strato di ozono (Protocollo Montreal)', 'Il protocollo di Montreal (alla convenzione di Vienna per la protezione dello strato di ozono) è un accordo globale volto a proteggere lo strato di ozono stratosferico terrestre eliminando gradualmente le sostanze chimiche che lo riducono. Tale progressiva riduzione riguarda sia la produzione che il consumo di sostanze che riducono lo strato di ozono (ODS). Il suo emendamento più recente, l’emendamento Kigali, richiede la graduale riduzione graduale degli HFC.', 'Un protocollo è un tipo di accordo formale tra Stati o altre entità sovrane che integra, modifica o specifica ulteriormente un trattato esistente. I protocolli sono strumenti giuridici che possono avere diverse funzioni e scopi, tra cui l''espansione di disposizioni di un trattato, la creazione di nuovi obblighi o la regolamentazione di questioni specifiche non completamente coperte dal trattato originale.', 'Protocollo relativo a sostanze che riducono lo strato di ozono', 'Protocollo - Sostanze che riducono lo strato di ozono (Protocollo Montreal)', 'Protocollo Montreal', 'Protocollo relativo a sostanze che riducono lo strato di ozono');

-- Record 904/2644
