INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (512, 'GRI 303- Acqua ed effluenti (2018)', 'GRI', '303', 'Questo Standard tratta il tema dell''acqua e degli effluenti. Contiene informative per le organizzazioni che devono rendicontare informazioni sui loro impatti correlati all''acqua e la relativa gestione.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Acqua ed effluenti (2018)', 'GRI - 303 (2018)', 'GRI 303-4 (2018)', 'Scarico idrico');

