INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (512, 'GRI 306- Scarichi idrici e rifiuti (2016)', 'GRI', '306', 'L''Informativa GRI 306 (2016) richiede alle organizzazioni di segnalare tutti gli "sversamenti significativi" di sostanze pericolose, come oli, prodotti chimici o altri materiali potenzialmente dannosi per l''ambiente. Uno "sversamento significativo" è definito come un evento che ha causato o potrebbe causare impatti ambientali negativi rilevanti, sia in termini di quantità di materiale sversato che di danno potenziale o effettivo.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Scarichi idrici e rifiuti (2016)', 'GRI - 306 (2016)', 'GRI 306-3 (2016)', 'Sversamenti significativi');

