INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (512, 'Framework globale sulla biodiversità Kunming-Montreal (GBF)- Target 15', 'Framework globale', 'Biodiversità (Kunming-Montreal) - Target 15', 'Adottare misure legali, amministrative o politiche per incoraggiare e abilitare le imprese, e in particolare garantire che le grandi aziende transnazionali e le istituzioni finanziarie: (a) monitorino regolarmente, valutino e divulghino in modo trasparente i loro rischi, le loro dipendenze e gli impatti sulla biodiversità, compresi i requisiti per tutte le grandi aziende e istituzioni finanziarie transnazionali lungo le loro operazioni, catene di fornitura e di valore, e portafogli; (b) forniscano informazioni necessarie ai consumatori per promuovere modelli di consumo sostenibili; (c) Riportino la conformità alle normative e misure di accesso e ripartizione dei benefici, ove applicabile; al fine di ridurre progressivamente gli impatti negativi sulla biodiversità, aumentare gli impatti positivi, ridurre i rischi legati alla biodiversità per le imprese e le istituzioni finanziarie e promuovere azioni per garantire modelli di produzione sostenibili.', 'Un "framework globale" si riferisce a un insieme di principi, linee guida, regole o standard internazionalmente riconosciuti e accettati che mirano a indirizzare e coordinare le azioni dei diversi paesi, organizzazioni internazionali, aziende e altre parti interessate su una determinata questione.', 'Target 15 (2030): Le imprese valutano, divulghino e riducono i rischi e gli impatti negativi legati alla biodiversità.', 'Framework globale - Biodiversità (Kunming-Montreal) - Target 15', 'GBF- Target 15 (2030)', 'Le imprese valutano, divulghino e riducono i rischi e gli impatti negativi legati alla biodiversità.');

-- Record 967/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (512, 'Decreto Legislativo 6 settembre 2024, n. 125 Attuazione della direttiva 2022/2464/UE del Parlamento europeo e del Consiglio del 14 dicembre 2022, recante modifica del regolamento 537/2014/UE, della direttiva 2004/109/CE, della direttiva 2006/43/CE e della direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Decreto (IT)', '125/2024', 'Il Decreto legislativo 6 settembre 2024, n. 125 attua la Direttiva 2022/2464/UE (nota come Corporate Sustainability Reporting Directive, CSRD). Questo decreto introduce nuovi obblighi di rendicontazione societaria di sostenibilità per le imprese, ampliando notevolmente le informazioni che devono essere incluse nei bilanci aziendali in materia di sostenibilità ambientale, sociale e di governance (ESG).', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Attuazione della direttiva 2022/2464/UE (CSRD)', 'Decreto Legislativo - 125/2024', 'D.Lgs. 125/2024', 'Attuazione della direttiva 2022/2464/UE (CSRD)');

-- Record 968/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (513, 'SDGs: Goal 14 La vita sott’acqua', 'SDGs', 'Goal 14', 'Conservare e utilizzare in modo durevole gli oceani, i mari e le risorse marine per uno sviluppo sostenibile', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'La vita sott’acqua', 'SDGs - Goal 14', 'SDG 14.2', 'Entro il 2020, gestire in modo sostenibile e proteggere l’ecosistema marino e costiero per evitare impatti particolarmente negativi, anche rafforzando la loro resilienza, e agire per il loro ripristino in modo da ottenere oceani salubri e produttivi');

-- Record 969/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (513, 'ESRS E4- Biodiversità ed ecosistemi', 'ESRS', 'E4', 'Questo Standard stabilisce i requisiti di Informativa relativi alla relazione dell''impresa con gli habitat terrestri, d''acqua dolce e marini, gli ecosistemi e le popolazioni di specie faunistiche e floristiche correlate, inclusa la diversità all''interno delle specie, tra le specie e degli ecosistemi, e la loro interrelazione con i popoli indigeni e altre comunità colpite.
 
 I termini "biodiversità" e "diversità biologica" si riferiscono alla variabilità tra gli organismi viventi di tutte le provenienze, compresi, tra l''altro, gli ecosistemi terrestri, d''acqua dolce, marini e altri ecosistemi acquatici e i complessi ecologici di cui fanno parte.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Biodiversità ed ecosistemi', 'ESRS - E4', 'ESRS (E4) E4-1 AR 1 d', 'Dichiarazione del contributo ai fattori di impatto e delle possibili azioni di mitigazione secondo la gerarchia di mitigazione, incluse le principali dipendenze strutturali e gli asset e le risorse vincolati associati ai cambiamenti della biodiversità e degli ecosistemi.');

-- Record 970/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (513, 'Framework globale sulla biodiversità Kunming-Montreal (GBF)- Target 15', 'Framework globale', 'Biodiversità (Kunming-Montreal) - Target 15', 'Adottare misure legali, amministrative o politiche per incoraggiare e abilitare le imprese, e in particolare garantire che le grandi aziende transnazionali e le istituzioni finanziarie: (a) monitorino regolarmente, valutino e divulghino in modo trasparente i loro rischi, le loro dipendenze e gli impatti sulla biodiversità, compresi i requisiti per tutte le grandi aziende e istituzioni finanziarie transnazionali lungo le loro operazioni, catene di fornitura e di valore, e portafogli; (b) forniscano informazioni necessarie ai consumatori per promuovere modelli di consumo sostenibili; (c) Riportino la conformità alle normative e misure di accesso e ripartizione dei benefici, ove applicabile; al fine di ridurre progressivamente gli impatti negativi sulla biodiversità, aumentare gli impatti positivi, ridurre i rischi legati alla biodiversità per le imprese e le istituzioni finanziarie e promuovere azioni per garantire modelli di produzione sostenibili.', 'Un "framework globale" si riferisce a un insieme di principi, linee guida, regole o standard internazionalmente riconosciuti e accettati che mirano a indirizzare e coordinare le azioni dei diversi paesi, organizzazioni internazionali, aziende e altre parti interessate su una determinata questione.', 'Target 15 (2030): Le imprese valutano, divulghino e riducono i rischi e gli impatti negativi legati alla biodiversità.', 'Framework globale - Biodiversità (Kunming-Montreal) - Target 15', 'GBF- Target 15 (2030)', 'Le imprese valutano, divulghino e riducono i rischi e gli impatti negativi legati alla biodiversità.');

-- Record 971/2644
