INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (513, 'Regolamento (UE) 2019/2088 del Parlamento europeo e del Consiglio del 27 novembre 2019 relativo all’informativa sulla sostenibilità nel settore dei servizi finanziari', 'Regolamento (UE)', '2019/2088', 'Questo regolamento stabilisce norme armonizzate sulla trasparenza per i partecipanti ai mercati finanziari e i consulenti finanziari per quanto riguarda l’integrazione dei rischi di sostenibilità e la considerazione degli effetti negativi per la sostenibilità nei loro processi e nella comunicazione delle informazioni connesse alla sostenibilità relative ai prodotti finanziari.', 'I regolamenti sono atti giuridici definiti nell’articolo 288 del trattato sul funzionamento dell’Unione europea (TFUE). Hanno portata generale, sono vincolanti in tutti i loro elementi e direttamente applicabili negli Stati membri dell’Unione. I regolamenti rientrano nel diritto derivato dell’Unione. Sono adottati dalle istituzioni dell’Unione europea sulla base dei trattati. Un regolamento ha carattere vincolante in tutti i suoi elementi, ha portata generale ed è rivolto a categorie astratte di persone, non a specifici individui. 
 https://eur-lex.europa.eu/IT/legal-content/summary/european-union-regulations.html', 'Regolamento relativo all’informativa sulla sostenibilità nel settore dei servizi finanziari', 'Regolamento (UE) - 2019/2088', 'Regolamento (UE) 2019/2088', NULL);

-- Record 980/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (513, 'Strategia per l’ambiente Marino (II ciclo 2018 – 2024)', 'Strategia Nazionale (IT)', 'Strategia per l’ambiente Marino', 'La Strategia per l''Ambiente Marino è un piano adottato dall''Italia per proteggere e preservare gli ecosistemi marini e costieri, che include tra gli obiettivi: protezione degli ecosistemi marini; riduzione dell''inquinamento; e adattamento ai cambiamenti climatici.', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Ambiente Marino (II ciclo 2018 – 2024)', 'Strategia Nazionale - Strategia per l’ambiente Marino', 'Strategia (IT) Ambiente Marino', NULL);

